            }

            .left-side-ad {
                left: 20px;
            }

            .right-side-ad {
                right: 20px;
            }

            @keyframes slideIn {
                from {
                    opacity: 0;
                    transform: translateY(-50%) scale(0.8);
                }
                to {
                    opacity: 1;
                    transform: translateY(-50%) scale(1);
                }
            }

            @keyframes slideOut {
                from {
                    opacity: 1;
                    transform: translateY(-50%) scale(1);
                }
                to {
                    opacity: 0;
                    transform: translateY(-50%) scale(0.8);
                }
            }

            .floating-side-ad:hover {
