    53→
    54→    // Create sidebar ad HTML
    55→    function createSidebarAd(ad) {
    56→        return `
    57→            <div class="fpl-sidebar-ad ${ad.special ? 'special-offer' : ''}" data-ad-id="${ad.id}">
    58→                <div class="fpl-ad-header" style="background: ${ad.color}">
    59→                    <h3 class="fpl-ad-title">${ad.title}</h3>
    60→                    <p class="fpl-ad-subtitle">${ad.subtitle}</p>
    61→                </div>
    62→                <div class="fpl-ad-body">
    63→                    <p class="fpl-ad-description">${ad.description}</p>
    64→                    <ul class="fpl-ad-features">
    65→                        ${ad.features.map(f => `<li>✓ ${f}</li>`).join('')}
    66→                    </ul>
    67→                    <a href="${ad.link}" class="fpl-ad-cta">
    68→                        ${ad.cta}
    69→                        <span class="cta-arrow">→</span>
    70→                    </a>
    71→                </div>
    72→                ${ad.special ? '<div class="special-badge">LIMITED OFFER</div>' : ''}
    73→            </div>
    74→        `;
    75→    }
    76→
    77→    // Create floating corner ad
    78→    function createFloatingAd() {
    79→        return `
    80→            <div class="fpl-floating-ad" id="fpl-floating-ad">
    81→                <button class="floating-close" onclick="this.parentElement.style.display='none'">×</button>
    82→                <div class="floating-content">
    83→                    <div class="floating-icon">🤖</div>
    84→                    <div class="floating-text">
    85→                        <strong>Need FPL Help?</strong>
    86→                        <span>AI Assistant is online!</span>
    87→                    </div>
    88→                </div>
    89→            </div>
    90→        `;
    91→    }
    92→
    93→    // Create big popup banner
    94→    function createBigBannerPopup() {
    95→        // Prioritize the premium bundle for maximum impact
    96→        const premiumAd = fplAds.find(ad => ad.id === 'premium-bundle-ad') || fplAds.filter(ad => ad.priority === 1)[0] || fplAds[0];
    97→        return `
    98→            <div class="fpl-big-banner-popup" id="fpl-big-banner-popup">
    99→                <div class="popup-overlay" onclick="closeBigBanner()"></div>
   100→                <div class="popup-content" style="background: ${premiumAd.color}">
   101→                    <button class="popup-close" onclick="closeBigBanner()">×</button>
   102→                    <div class="popup-inner">
   103→                        <div class="popup-icon">⚡</div>
   104→                        <h2 class="popup-title">${premiumAd.title}</h2>
   105→                        <p class="popup-subtitle">${premiumAd.subtitle}</p>
   106→                        <div class="popup-description">
   107→                            <p>${premiumAd.description}</p>
   108→                        </div>
   109→                        <div class="popup-features">
   110→                            ${premiumAd.features.map(f => `<div class="feature-item">✨ ${f}</div>`).join('')}
   111→                        </div>
   112→                        <div class="popup-cta-group">
   113→                            <a href="${premiumAd.link}" class="popup-cta-primary">${premiumAd.cta}</a>
   114→                            <button class="popup-cta-secondary" onclick="closeBigBanner()">Maybe Later</button>
   115→                        </div>
   116→                        ${premiumAd.special ? '<div class="popup-special-badge">🔥 LIMITED TIME OFFER - 50% OFF</div>' : ''}
   117→                    </div>
   118→                </div>
   119→            </div>
   120→        `;
   121→    }
   122→
   123→    // Close big banner function - redirects to homepage
   124→    window.closeBigBanner = function() {
   125→        const popup = document.getElementById('fpl-big-banner-popup');
   126→        if (popup) {
   127→            popup.classList.add('closing');
   128→            setTimeout(() => {
   129→                popup.remove();
   130→                // Re-enable body scroll
   131→                document.body.style.overflow = '';
   132→                // Redirect to homepage
   133→                window.location.href = '/';
   134→            }, 300);
   135→            // Set cookie to not show again for 24 hours
   136→            document.cookie = "fplBannerClosed=true; max-age=86400; path=/";
   137→        }
   138→    };
   139→
   140→    // Create banner ad for article pages
   141→    function createBannerAd() {
