    /* Transfer Statistics Section */
    .transfer-stats-section {
        background: linear-gradient(135deg, #1a1a2e, #0f0f1e);
        padding: 60px 20px;
        margin-top: 60px;
    }

    .transfer-stats-section h2 {
        text-align: center;
        color: white;
        font-size: 2.5rem;
        margin-bottom: 40px;
        text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
    }

    .stats-grid {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
        gap: 30px;
        max-width: 1400px;
        margin: 0 auto;
    }

    .stat-panel {
        background: rgba(255, 255, 255, 0.05);
        border: 1px solid rgba(0, 255, 135, 0.2);
        border-radius: 16px;
        padding: 30px;
        backdrop-filter: blur(10px);
    }

    .stat-panel h3 {
        color: #00ff87;
        font-size: 1.5rem;
        margin-bottom: 25px;
        display: flex;
        align-items: center;
        gap: 10px;
    }

    /* Top Spending Clubs */
    .spending-list {
        display: flex;
        flex-direction: column;
        gap: 20px;
    }

    .spending-item {
        display: grid;
        grid-template-columns: 30px 1fr;
        gap: 15px;
        align-items: center;
        position: relative;
    }

    .club-rank {
        width: 30px;
        height: 30px;
        background: linear-gradient(135deg, #ffd700, #ffed4e);
        color: #1a1a2e;
        border-radius: 50%;
        display: flex;
        align-items: center;
        justify-content: center;
        font-weight: 700;
        font-size: 0.9rem;
    }

    .club-info {
        display: flex;
        justify-content: space-between;
        align-items: center;
        z-index: 2;
    }

    .club-name {
        color: white;
        font-weight: 600;
        font-size: 1.1rem;
    }

    .club-spending {
        color: #00ff87;
        font-weight: 700;
        font-size: 1.2rem;
    }

    .spending-bar {
        position: absolute;
        left: 50px;
        right: 0;
        height: 35px;
        background: linear-gradient(90deg, rgba(0, 255, 135, 0.3), rgba(0, 255, 135, 0.1));
        border-radius: 8px;
        z-index: 1;
        transition: all 0.3s ease;
    }

    .spending-item:hover .spending-bar {
        background: linear-gradient(90deg, rgba(0, 255, 135, 0.5), rgba(0, 255, 135, 0.2));
    }

    /* Transfer Trends */
    .trend-stats {
        display: grid;
        grid-template-columns: 1fr;
        gap: 20px;
    }

    .trend-item {
        padding: 15px;
        background: rgba(255, 255, 255, 0.03);
        border-radius: 12px;
        border-left: 4px solid #00ff87;
        transition: all 0.3s ease;
    }

    .trend-item:hover {
        background: rgba(255, 255, 255, 0.08);
        transform: translateX(5px);
    }

    .trend-label {
        color: #888;
        font-size: 0.9rem;
        text-transform: uppercase;
        letter-spacing: 1px;
        margin-bottom: 5px;
    }

    .trend-value {
        color: white;
        font-size: 1.5rem;
        font-weight: 700;
        margin-bottom: 3px;
    }

    .trend-detail {
        color: #00ff87;
        font-size: 0.85rem;
        opacity: 0.8;
    }

    /* Transfer Routes */
    .routes-chart {
        margin-bottom: 20px;
    }

    .route-item {
        margin-bottom: 20px;
    }

    .route-name {
        color: white;
        font-weight: 600;
        margin-bottom: 8px;
        font-size: 1.1rem;
    }

    .route-bar-container {
        height: 35px;
        background: rgba(255, 255, 255, 0.05);
        border-radius: 20px;
        overflow: hidden;
        position: relative;
    }

    .route-bar {
        height: 100%;
        background: linear-gradient(90deg, #37003c, #6f42c1);
        border-radius: 20px;
        display: flex;
        align-items: center;
        justify-content: flex-end;
        padding-right: 15px;
        transition: all 0.5s ease;
        position: relative;
    }

    .route-bar span {
        color: white;
        font-weight: 700;
        font-size: 1rem;
    }

    .route-bar.saudi {
        background: linear-gradient(90deg, #00c851, #00ff6f);
    }

    .route-bar.mls {
        background: linear-gradient(90deg, #ff4444, #ff6666);
    }

    .route-bar:hover {
        transform: scaleX(1.02);
    }

    .route-legend {
        display: flex;
        gap: 20px;
        justify-content: center;
        flex-wrap: wrap;
        padding-top: 20px;
        border-top: 1px solid rgba(255, 255, 255, 0.1);
    }

    .legend-item {
        display: flex;
        align-items: center;
        gap: 8px;
        color: white;
        font-size: 0.9rem;
    }

    .legend-color {
        width: 20px;
        height: 20px;
        border-radius: 4px;
    }

    @media (max-width: 768px) {
        .transfer-stats-section {
            padding: 40px 15px;
        }

        .stats-grid {
            grid-template-columns: 1fr;
            gap: 20px;
        }

        .stat-panel {
            padding: 20px;
        }

        .transfer-stats-section h2 {
            font-size: 1.8rem;
        }

        .stat-panel h3 {
            font-size: 1.2rem;
        }
    }
</style>

