   264→    function headerSignOut() {
   265→        // Clear user data
   266→        localStorage.removeItem('user');
   267→        localStorage.removeItem('membership');
   268→        localStorage.removeItem('eplMembership');
   269→        localStorage.removeItem('authToken');
   270→        
   271→        // Hide account widget
   272→        hideHeaderAccountWidget();
   273→        
   274→        // Update header state
   275→        showSignedOutState();
   276→        
   277→        // Show notification
   278→        showHeaderNotification('Signed out successfully!', 'success');
   279→        
   280→        // Reload page to reset state
   281→        setTimeout(() => {
   282→            window.location.reload();
   283→        }, 1000);
   284→    }
