  1693→
  1694→<script>
  1695→    // Enhanced Search Functionality
  1696→    let searchTimeout;
  1697→    let isSearching = false;
  1698→    let searchCache = new Map();
  1699→    
  1700→    // Initialize search functionality
  1701→    document.addEventListener('DOMContentLoaded', function() {
  1702→        initializeSearch();
  1703→    });
  1704→    
  1705→    function initializeSearch() {
  1706→        const searchInput = document.getElementById('search-input');
  1707→        const searchBtn = document.querySelector('.search-btn');
  1708→        const searchClear = document.querySelector('.search-clear');
  1709→        
  1710→        if (searchInput) {
  1711→            // Add event listeners
  1712→            searchInput.addEventListener('input', handleSearchInput);
  1713→            searchInput.addEventListener('keydown', handleSearchKeydown);
  1714→            searchInput.addEventListener('focus', handleSearchFocus);
  1715→            searchInput.addEventListener('blur', handleSearchBlur);
  1716→        }
  1717→        
  1718→        if (searchClear) {
  1719→            searchClear.addEventListener('click', clearSearch);
  1720→        }
  1721→        
  1722→        // Close dropdown when clicking outside
  1723→        document.addEventListener('click', function(event) {
  1724→            const searchContainer = document.querySelector('.search-container');
  1725→            if (searchContainer && !searchContainer.contains(event.target)) {
  1726→                hideSearchResults();
  1727→            }
  1728→        });
  1729→    }
  1730→    
  1731→    function handleSearchInput(event) {
  1732→        const query = event.target.value;
  1733→        const searchClear = document.querySelector('.search-clear');
  1734→        
  1735→        // Show/hide clear button
  1736→        if (searchClear) {
  1737→            searchClear.style.display = query.length > 0 ? 'flex' : 'none';
  1738→        }
  1739→        
  1740→        // Debounced search
  1741→        clearTimeout(searchTimeout);
  1742→        if (query.length >= 2) {
  1743→            searchTimeout = setTimeout(() => {
  1744→                performInstantSearch(query);
  1745→            }, 300);
  1746→        } else {
  1747→            hideSearchResults();
  1748→        }
  1749→    }
  1750→    
  1751→    function handleSearchKeydown(event) {
  1752→        if (event.key === 'Enter') {
  1753→            event.preventDefault();
  1754→            const query = event.target.value.trim();
  1755→            if (query) {
  1756→                performSearch(query);
  1757→            }
  1758→        } else if (event.key === 'Escape') {
  1759→            hideSearchResults();
  1760→            event.target.blur();
  1761→        }
  1762→    }
  1763→    
  1764→    function handleSearchFocus(event) {
  1765→        const query = event.target.value;
  1766→        if (query.length >= 2) {
  1767→            performInstantSearch(query);
  1768→        }
  1769→    }
  1770→    
  1771→    function handleSearchBlur(event) {
  1772→        // Delay hiding to allow click events on results
  1773→        setTimeout(() => {
  1774→            if (!document.querySelector('.search-results-dropdown:hover')) {
  1775→                hideSearchResults();
  1776→            }
  1777→        }, 200);
  1778→    }
  1779→    
  1780→    function clearSearch() {
  1781→        const searchInput = document.getElementById('search-input');
  1782→        const searchClear = document.querySelector('.search-clear');
  1783→        
  1784→        if (searchInput) {
  1785→            searchInput.value = '';
  1786→            searchInput.focus();
  1787→        }
  1788→        if (searchClear) {
  1789→            searchClear.style.display = 'none';
  1790→        }
  1791→        hideSearchResults();
  1792→    }
  1793→    
  1794→    function performInstantSearch(query) {
  1795→        if (isSearching) return;
  1796→        
  1797→        // Check cache first
  1798→        if (searchCache.has(query)) {
  1799→            displaySearchResults(searchCache.get(query), query);
  1800→            return;
  1801→        }
  1802→        
  1803→        isSearching = true;
  1804→        showSearchLoading();
  1805→        
  1806→        // Simulate API call delay
  1807→        setTimeout(() => {
  1808→            const results = searchArticles(query);
  1809→            searchCache.set(query, results);
  1810→            displaySearchResults(results, query);
  1811→            isSearching = false;
  1812→        }, 150);
  1813→    }
  1814→    
  1815→    function performSearch(query) {
  1816→        if (!query) {
  1817→            query = document.getElementById('search-input').value.trim();
  1818→        }
  1819→        
  1820→        if (!query) return;
  1821→        
  1822→        // For now, just show instant results
  1823→        // In a real app, this would navigate to a full search results page
  1824→        performInstantSearch(query);
  1825→        
  1826→        // Optional: Navigate to full search page
  1827→        // window.location.href = `/search.html?q=${encodeURIComponent(query)}`;
  1828→    }
  1829→    
  1830→    function searchArticles(query) {
  1831→        // Article database (in real app, this would be fetched from an API)
  1832→        const articles = [
  1833→            {
  1834→                title: "Arsenal Transfer News: Latest Updates and Rumors",
  1835→                snippet: "Arsenal are reportedly interested in signing a new striker this summer transfer window...",
  1836→                url: "/articles/arsenal-transfer-news-latest-updates.html",
  1837→                date: "2025-01-12",
  1838→                category: "Transfer News"
  1839→            },
  1840→            {
  1841→                title: "Manchester United vs Liverpool: Match Preview",
  1842→                snippet: "The biggest rivalry in English football returns as United host Liverpool at Old Trafford...",
