   144â†’
   145â†’    function checkHeaderAuthState() {
   146â†’        // Check for user data in localStorage
   147â†’        const userData = localStorage.getItem('user');
   148â†’        
   149â†’        if (userData) {
   150â†’            try {
   151â†’                const user = JSON.parse(userData);
   152â†’                showSignedInState(user);
   153â†’                showHeaderAccountWidget(user);
   154â†’            } catch (error) {
   155â†’                console.error('Error parsing user data:', error);
   156â†’                showSignedOutState();
   157â†’                hideHeaderAccountWidget();
   158â†’            }
   159â†’        } else {
   160â†’            showSignedOutState();
   161â†’            hideHeaderAccountWidget();
   162â†’        }
   163â†’    }
   164â†’
   165â†’    function showSignedInState(user) {
   166â†’        // Hide sign in/create account links
   167â†’        const signinLink = document.getElementById('signin-link');
   168â†’        const createAccountLink = document.getElementById('create-account-link');
   169â†’        const mobileSigninLink = document.getElementById('mobile-signin-link');
   170â†’        const mobileCreateAccountLink = document.getElementById('mobile-create-account-link');
   171â†’        
   172â†’        if (signinLink) signinLink.classList.add('hidden');
   173â†’        if (createAccountLink) createAccountLink.classList.add('hidden');
   174â†’        if (mobileSigninLink) mobileSigninLink.classList.add('hidden');
   175â†’        if (mobileCreateAccountLink) mobileCreateAccountLink.classList.add('hidden');
   176â†’        
   177â†’        // Show account and premium links
   178â†’        const accountLink = document.getElementById('account-link');
   179â†’        const premiumLink = document.getElementById('premium-link');
   180â†’        const mobileAccountLink = document.getElementById('mobile-account-link');
   181â†’        const mobilePremiumLink = document.getElementById('mobile-premium-link');
   182â†’        
   183â†’        if (accountLink) accountLink.classList.remove('hidden');
   184â†’        if (premiumLink) premiumLink.classList.remove('hidden');
   185â†’        if (mobileAccountLink) mobileAccountLink.classList.remove('hidden');
   186â†’        if (mobilePremiumLink) mobilePremiumLink.classList.remove('hidden');
   187â†’    }
   188â†’
   189â†’    function showSignedOutState() {
   190â†’        // Show sign in/create account links
   191â†’        const signinLink = document.getElementById('signin-link');
   192â†’        const createAccountLink = document.getElementById('create-account-link');
   193â†’        const mobileSigninLink = document.getElementById('mobile-signin-link');
   194â†’        const mobileCreateAccountLink = document.getElementById('mobile-create-account-link');
   195â†’        
   196â†’        if (signinLink) signinLink.classList.remove('hidden');
   197â†’        if (createAccountLink) createAccountLink.classList.remove('hidden');
   198â†’        if (mobileSigninLink) mobileSigninLink.classList.remove('hidden');
   199â†’        if (mobileCreateAccountLink) mobileCreateAccountLink.classList.remove('hidden');
   200â†’        
   201â†’        // Hide account and premium links
   202â†’        const accountLink = document.getElementById('account-link');
   203â†’        const premiumLink = document.getElementById('premium-link');
   204â†’        const mobileAccountLink = document.getElementById('mobile-account-link');
   205â†’        const mobilePremiumLink = document.getElementById('mobile-premium-link');
   206â†’        
   207â†’        if (accountLink) accountLink.classList.add('hidden');
   208â†’        if (premiumLink) premiumLink.classList.add('hidden');
   209â†’        if (mobileAccountLink) mobileAccountLink.classList.add('hidden');
   210â†’        if (mobilePremiumLink) mobilePremiumLink.classList.add('hidden');
   211â†’    }
   212â†’
   213â†’    function checkPremiumMembershipForAds() {
   214â†’        try {
   215â†’            // Check for membership data
   216â†’            const membershipData = localStorage.getItem('membership') || localStorage.getItem('eplMembership');
   217â†’            
   218â†’            if (membershipData) {
   219â†’                const membership = JSON.parse(membershipData);
   220â†’                
   221â†’                // If user has premium membership, hide ads
   222â†’                if (membership.plan && membership.plan !== 'free') {
   223â†’                    hidePremiumAds();
   224â†’                    showAdFreeIndicator();
   225â†’                }
   226â†’            }
   227â†’        } catch (error) {
   228â†’            console.error('Error checking membership for ads:', error);
   229â†’        }
   230â†’    }
   231â†’
   232â†’    function hidePremiumAds() {
   233â†’        // Hide Google AdSense ads
   234â†’        const adElements = document.querySelectorAll('.adsbygoogle, [class*="ad-"], [id*="ad-"], [class*="advertisement"], [class*="google-ads"]');
   235â†’        adElements.forEach(ad => {
   236â†’            ad.style.display = 'none';
   237â†’            ad.style.visibility = 'hidden';
   238â†’        });
   239â†’
   240â†’        // Also hide any ad containers or wrappers
   241â†’        const adContainers = document.querySelectorAll('[class*="ad-container"], [class*="ad-wrapper"], [id*="ad-container"], [id*="ad-wrapper"]');
   242â†’        adContainers.forEach(container => {
   243â†’            container.style.display = 'none';
   244â†’        });
   245â†’
   246â†’        // Add a class to body to indicate ad-free mode
   247â†’        document.body.classList.add('ad-free-premium');
   248â†’        
   249â†’        console.log('Premium ad-free experience activated');
   250â†’    }
