   286â†’    function showHeaderNotification(message, type = 'info') {
   287â†’        const notification = document.createElement('div');
   288â†’        notification.className = `header-notification ${type}`;
   289â†’        notification.innerHTML = `
   290â†’            <span>${message}</span>
   291â†’            <button onclick="this.parentElement.remove()">Ã—</button>
   292â†’        `;
   293â†’        notification.style.cssText = `
   294â†’            position: fixed;
   295â†’            top: 20px;
   296â†’            right: 20px;
   297â†’            background: ${type === 'success' ? '#28a745' : type === 'error' ? '#dc3545' : '#007bff'};
   298â†’            color: white;
   299â†’            padding: 15px 20px;
   300â†’            border-radius: 8px;
   301â†’            box-shadow: 0 4px 15px rgba(0,0,0,0.2);
   302â†’            z-index: 10000;
   303â†’            display: flex;
   304â†’            align-items: center;
   305â†’            gap: 10px;
   306â†’            font-weight: 600;
   307â†’            animation: slideInRight 0.3s ease;
   308â†’        `;
   309â†’        
   310â†’        document.body.appendChild(notification);
   311â†’        
   312â†’        // Auto-remove after 3 seconds
   313â†’        setTimeout(() => {
   314â†’            if (notification.parentElement) {
   315â†’                notification.style.animation = 'slideOutRight 0.3s ease';
   316â†’                setTimeout(() => notification.remove(), 300);
   317â†’            }
   318â†’        }, 3000);
   319â†’    }
