            function checkAccountStatus() {
                const userData = localStorage.getItem('user');
                const accountWidget = document.getElementById('account-dashboard-widget');
                const signinWidget = document.getElementById('signin-dashboard-widget');

                if (userData && accountWidget) {
                    try {
                        const user = JSON.parse(userData);
                        showAccountWidget(user);
                        hideSigninWidget();
                    } catch (error) {
                        console.error('Error parsing user data:', error);
                        hideAccountWidget();
                        showSigninWidget();
                    }
                } else {
                    hideAccountWidget();
                    showSigninWidget();
                }
