                    accountWidget.classList.add('hidden');
                }
            }
            function showSigninWidget() {
                const signinWidget = document.getElementById('signin-dashboard-widget');
                if (signinWidget) {
                    signinWidget.classList.remove('hidden');
                }
            }
            function hideSigninWidget() {
                const signinWidget = document.getElementById('signin-dashboard-widget');
                if (signinWidget) {
                    signinWidget.classList.add('hidden');
                }
            }
            function signOut() {
                // Clear user data
                localStorage.removeItem('user');
                localStorage.removeItem('membership');
                localStorage.removeItem('authToken');
                sessionStorage.clear();

                // Hide account widget and show signin widget
                hideAccountWidget();
                showSigninWidget();

