    async sendSubscriptionToServer(subscription) {
        try {
            const response = await fetch('/api/push-notification/subscribe', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    subscription: subscription,
                    userId: this.getCurrentUserId()
                })
            });

