  1717‚Üí        
  1718‚Üí        if (searchInput) {
  1719‚Üí            // Add event listeners
  1720‚Üí            searchInput.addEventListener('input', handleSearchInput);
  1721‚Üí            searchInput.addEventListener('keydown', handleSearchKeydown);
  1722‚Üí            searchInput.addEventListener('focus', handleSearchFocus);
  1723‚Üí            searchInput.addEventListener('blur', handleSearchBlur);
  1724‚Üí        }
  1725‚Üí        
  1726‚Üí        if (searchClear) {
  1727‚Üí            searchClear.addEventListener('click', clearSearch);
