  1731‚Üí        document.addEventListener('click', function(event) {
  1732‚Üí            const searchContainer = document.querySelector('.search-container');
  1733‚Üí            if (searchContainer && !searchContainer.contains(event.target)) {
  1734‚Üí                hideSearchResults();
  1735‚Üí            }
  1736‚Üí        });
  1737‚Üí    }
  1738‚Üí    
  1739‚Üí    function handleSearchInput(event) {
  1740‚Üí        const query = event.target.value;
  1741‚Üí        const searchClear = document.querySelector('.search-clear');
  1742‚Üí        
  1743‚Üí        // Show/hide clear button
  1744‚Üí        if (searchClear) {
  1745‚Üí            searchClear.style.display = query.length > 0 ? 'flex' : 'none';
  1746‚Üí        }
  1747‚Üí        
  1748‚Üí        // Debounced search
  1749‚Üí        clearTimeout(searchTimeout);
  1750‚Üí        if (query.length >= 2) {
  1751‚Üí            searchTimeout = setTimeout(() => {
  1752‚Üí                performInstantSearch(query);
  1753‚Üí            }, 300);
  1754‚Üí        } else {
  1755‚Üí            hideSearchResults();
  1756‚Üí        }
  1757‚Üí    }
  1758‚Üí    
  1759‚Üí    function handleSearchKeydown(event) {
  1760‚Üí        if (event.key === 'Enter') {
  1761‚Üí            event.preventDefault();
  1762‚Üí            const query = event.target.value.trim();
  1763‚Üí            if (query) {
  1764‚Üí                performSearch(query);
  1765‚Üí            }
  1766‚Üí        } else if (event.key === 'Escape') {
  1767‚Üí            hideSearchResults();
  1768‚Üí            event.target.blur();
  1769‚Üí        }
  1770‚Üí    }
  1771‚Üí    
  1772‚Üí    function handleSearchFocus(event) {
  1773‚Üí        const query = event.target.value;
  1774‚Üí        if (query.length >= 2) {
  1775‚Üí            performInstantSearch(query);
  1776‚Üí        }
  1777‚Üí    }
  1778‚Üí    
  1779‚Üí    function handleSearchBlur(event) {
  1780‚Üí        // Delay hiding to allow click events on results
  1781‚Üí        setTimeout(() => {
  1782‚Üí            if (!document.querySelector('.search-results-dropdown:hover')) {
  1783‚Üí                hideSearchResults();
  1784‚Üí            }
  1785‚Üí        }, 200);
  1786‚Üí    }
  1787‚Üí    
  1788‚Üí    function clearSearch() {
  1789‚Üí        const searchInput = document.getElementById('search-input');
  1790‚Üí        const searchClear = document.querySelector('.search-clear');
  1791‚Üí        
  1792‚Üí        if (searchInput) {
  1793‚Üí            searchInput.value = '';
  1794‚Üí            searchInput.focus();
  1795‚Üí        }
  1796‚Üí        if (searchClear) {
  1797‚Üí            searchClear.style.display = 'none';
  1798‚Üí        }
  1799‚Üí        hideSearchResults();
  1800‚Üí    }
  1801‚Üí    
  1802‚Üí    function performInstantSearch(query) {
  1803‚Üí        if (isSearching) return;
  1804‚Üí        
  1805‚Üí        // Check cache first
  1806‚Üí        if (searchCache.has(query)) {
  1807‚Üí            displaySearchResults(searchCache.get(query), query);
  1808‚Üí            return;
  1809‚Üí        }
  1810‚Üí        
  1811‚Üí        isSearching = true;
  1812‚Üí        showSearchLoading();
  1813‚Üí        
  1814‚Üí        // Simulate API call delay
  1815‚Üí        setTimeout(() => {
  1816‚Üí            const results = searchArticles(query);
  1817‚Üí            searchCache.set(query, results);
  1818‚Üí            displaySearchResults(results, query);
  1819‚Üí            isSearching = false;
  1820‚Üí        }, 150);
  1821‚Üí    }
  1822‚Üí    
  1823‚Üí    function performSearch(query) {
