  1962‚Üí    
  1963‚Üí    function formatDate(dateStr) {
  1964‚Üí        const date = new Date(dateStr);
  1965‚Üí        const now = new Date();
  1966‚Üí        const diff = now - date;
  1967‚Üí        const days = Math.floor(diff / (1000 * 60 * 60 * 24));
  1968‚Üí        
  1969‚Üí        if (days === 0) return 'Today';
  1970‚Üí        if (days === 1) return 'Yesterday';
  1971‚Üí        if (days < 7) return `${days} days ago`;
  1972‚Üí        return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  1973‚Üí    }
