}

.headline-item:hover {
    border-left-color: #0066cc;
    box-shadow: 0 3px 10px rgba(0,0,0,0.1);
    transform: translateX(5px);
}

.headline-category {
    background: #0066cc;
    color: white;
    padding: 4px 10px;
    border-radius: 12px;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    flex-shrink: 0;
}

.headline-item a {
    color: #121212;
    text-decoration: none;
    font-weight: 500;
    transition: color 0.2s ease;
}

.headline-item:hover a {
    color: #0066cc;
}

/* Trending Section */
.trending-section {
    background: linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%);
    padding: 25px;
    border-radius: 12px;
    margin-bottom: 30px;
    border: 1px solid #e9ecef;
    box-shadow: 0 2px 8px rgba(0,0,0,0.05);
}

.trending-section h3 {
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 1.2rem;
    font-weight: 700;
    color: #121212;
    margin-bottom: 20px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.trending-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.trending-list li {
    margin-bottom: 15px;
    padding-bottom: 15px;
    border-bottom: 1px solid #e2e2e2;
}

.trending-list li:last-child {
    border-bottom: none;
    margin-bottom: 0;
    padding-bottom: 0;
}

.trending-list a {
    color: #121212;
    text-decoration: none;
    font-family: 'Georgia', serif;
    font-size: 0.95rem;
    line-height: 1.4;
    transition: color 0.2s ease;
    display: flex;
    align-items: center;
    gap: 12px;
}

.trending-list a:hover {
    color: #0066cc;
}

.trending-number {
    background: #0066cc;
    color: white;
    width: 24px;
    height: 24px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 700;
    font-size: 0.8rem;
    flex-shrink: 0;
}

.trending-text {
    flex: 1;
}





/* Article Styling */
.main_headline {
    margin-bottom: 30px;
    background: linear-gradient(145deg, #ffffff 0%, #f8f9fa 100%);
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0 6px 30px rgba(0,0,0,0.1);
    transition: all 0.3s ease;
}

.main_headline:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 40px rgba(0,0,0,0.15);
}

.main_headline a {
    text-decoration: none;
    color: inherit;
    display: block;
    transition: all 0.2s ease;
}

.main_headline a:hover {
    opacity: 1;
}

.main_headline img {
    width: 100%;
    height: auto;
    max-height: 450px;
    object-fit: cover;
    display: block;
    transition: transform 0.3s ease;
    border-radius: 12px;
}

.main_headline:hover img {
    transform: scale(1.02);
}

.main_headline h1 {
    font-size: 2.4rem;
    font-weight: 800;
    line-height: 1.15;
    margin: 20px 25px 15px 25px;
    color: #121212;
    transition: color 0.2s ease;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
}

.main_headline a:hover h1 {
    color: #0066cc;
}

.main_headline p {
    font-size: 1.15rem;
    color: #555;
    line-height: 1.65;
    font-family: 'Georgia', serif;
    margin: 0 25px 25px 25px;
    padding-bottom: 5px;
}

.main_subheadline {
    margin-bottom: 25px;
    padding: 20px 0;
    border-bottom: 1px solid #f0f0f0;
}

.main_subheadline:last-child {
    border-bottom: none;
}

.main_subheadline a {
    text-decoration: none;
    color: inherit;
    display: flex;
    gap: 20px;
    align-items: flex-start;
    transition: all 0.2s ease;
}

.main_subheadline a:hover {
    opacity: 0.95;
}

.main_subheadline img {
    width: 200px;
    height: 150px;
    object-fit: cover;
    flex-shrink: 0;
    border-radius: 10px;
}

.main_subheadline h1 {
    font-size: 1.4rem;
    font-weight: 600;
    line-height: 1.3;
    margin-bottom: 10px;
    color: #121212;
    transition: color 0.2s ease;
}

.main_subheadline a:hover h1 {
    color: #0066cc;
}

.main_subheadline p {
    font-size: 0.95rem;
    color: #666;
    line-height: 1.5;
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.headlinelink a {
    color: #121212;
    text-decoration: none;
    transition: color 0.2s ease;
}

.headlinelink a:hover {
    color: #0066cc;
}

.headlinelink {
    color: #121212;
    text-decoration: none;
    display: block;
}

.headlinelink h1 {
    color: #121212;
    text-decoration: none;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
    font-weight: 800;
    letter-spacing: -0.02em;
}


/* Modern Footer Styling */
.modern-footer {
    background: linear-gradient(135deg, #0f0f23 0%, #1a1a2e 50%, #2d2d2d 100%);
    color: #ffffff;
    margin-top: 50px;
    position: relative;
    overflow: hidden;
    animation: fadeInUp 0.8s ease-out;
}

.modern-footer::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent 0%, #37003c 20%, #00ff87 50%, #37003c 80%, transparent 100%);
    opacity: 0.6;
    animation: shimmer 3s ease-in-out infinite;
}

.modern-footer::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(ellipse at 20% 50%, rgba(55, 0, 60, 0.1) 0%, transparent 50%),
                radial-gradient(ellipse at 80% 20%, rgba(0, 255, 135, 0.05) 0%, transparent 50%);
    pointer-events: none;
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes shimmer {
    0%, 100% {
        opacity: 0.6;
    }
    50% {
        opacity: 1;
    }
}

