        margin-bottom: 8px;
    }

    .nyt-article .byline {
        font-size: 0.85rem;
        margin-bottom: 15px;
    }


    /* Mobile Performance Optimizations */
    * {
        -webkit-tap-highlight-color: transparent;
    }

    img {
