    gap: 2rem;
    margin-top: 2rem;
}

.news-article-card {
    border: 1px solid #e2e2e2;
    border-radius: 8px;
    padding: 1.5rem;
    background: #ffffff;
    transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.news-article-card:hover {
