}

.news-article-card h2 a:hover {
    color: #0066cc;
}

.article-excerpt {
    color: #666;
    font-size: 0.95rem;
    margin-bottom: 1rem;
}

.article-meta {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.85rem;
    color: #888;
