.footer-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

.footer-main {
    display: grid;
    grid-template-columns: auto 1fr auto;
    gap: 40px;
    align-items: center;
    margin-bottom: 15px;
    padding-bottom: 15px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.footer-brand h3 {
    font-size: 1.5rem;
    font-weight: 800;
    margin: 0 0 5px 0;
    background: linear-gradient(135deg, #ffffff 0%, #00ff87 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.footer-tagline {
    font-size: 0.8rem;
    color: #ccc;
    font-weight: 500;
}

.footer-links {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 30px;
    text-align: center;
}

.footer-section {
    display: flex;
    gap: 15px;
    justify-content: center;
}

.footer-section a {
    color: #ccc;
    text-decoration: none;
    font-size: 0.9rem;
    font-weight: 500;
    transition: color 0.3s ease;
    padding: 5px 10px;
    border-radius: 4px;
}

.footer-section a:hover {
    color: #00ff87;
    background: rgba(0, 255, 135, 0.1);
}

.footer-social {
    display: flex;
    gap: 12px;
    align-items: center;
}

.footer-social a {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 35px;
    height: 35px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    color: #ccc;
    text-decoration: none;
    transition: all 0.3s ease;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.footer-social a:hover {
    background: rgba(0, 255, 135, 0.2);
    color: #00ff87;
    border-color: #00ff87;
    transform: translateY(-2px);
}

.footer-bottom {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.85rem;
    color: #999;
    flex-wrap: wrap;
    gap: 15px;
}

.footer-badges {
    display: flex;
    gap: 12px;
    flex-wrap: wrap;
}

.footer-badges span {
    font-size: 0.8rem;
    padding: 3px 8px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

/* Mobile responsive */
@media (max-width: 768px) {
    .footer-main {
        grid-template-columns: 1fr;
        text-align: center;
        gap: 20px;
    }

    .footer-links {
        grid-template-columns: 1fr;
        gap: 15px;
    }

    .footer-section {
        flex-wrap: wrap;
        gap: 10px;
    }

    .footer-bottom {
        flex-direction: column;
        text-align: center;
        gap: 10px;
    }

    .footer-badges {
        justify-content: center;
        gap: 8px;
    }
}

