/* Article Styling */
.main_headline {
    margin-bottom: 30px;
    background: linear-gradient(145deg, #ffffff 0%, #f8f9fa 100%);
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0 6px 30px rgba(0,0,0,0.1);
    transition: all 0.3s ease;
}

.main_headline:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 40px rgba(0,0,0,0.15);
}

.main_headline a {
    text-decoration: none;
    color: inherit;
    display: block;
    transition: all 0.2s ease;
}

.main_headline a:hover {
    opacity: 1;
}

.main_headline img {
    width: 100%;
    height: auto;
    max-height: 450px;
    object-fit: cover;
    display: block;
    transition: transform 0.3s ease;
    border-radius: 12px;
}

.main_headline:hover img {
