}

.main_headline p {
    font-size: 1.15rem;
    color: #555;
    line-height: 1.65;
    font-family: 'Georgia', serif;
    margin: 0 25px 25px 25px;
    padding-bottom: 5px;
}

.main_subheadline {
    margin-bottom: 25px;
    padding: 20px 0;
    border-bottom: 1px solid #f0f0f0;
}

.main_subheadline:last-child {
    border-bottom: none;
}

.main_subheadline a {
    text-decoration: none;
    color: inherit;
    display: flex;
    gap: 20px;
    align-items: flex-start;
    transition: all 0.2s ease;
}

.main_subheadline a:hover {
    opacity: 0.95;
}

.main_subheadline img {
    width: 200px;
    height: 150px;
    object-fit: cover;
    flex-shrink: 0;
    border-radius: 10px;
}

.main_subheadline h1 {
    font-size: 1.4rem;
    font-weight: 600;
    line-height: 1.3;
    margin-bottom: 10px;
    color: #121212;
    transition: color 0.2s ease;
}

.main_subheadline a:hover h1 {
    color: #0066cc;
}

.main_subheadline p {
    font-size: 0.95rem;
    color: #666;
    line-height: 1.5;
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.headlinelink a {
    color: #121212;
    text-decoration: none;
    transition: color 0.2s ease;
}

.headlinelink a:hover {
    color: #0066cc;
}

.headlinelink {
    color: #121212;
    text-decoration: none;
    display: block;
}

.headlinelink h1 {
    color: #121212;
    text-decoration: none;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
