/* Mobile responsive */
@media (max-width: 768px) {
    .footer-main {
        grid-template-columns: 1fr;
        text-align: center;
        gap: 20px;
    }

    .footer-links {
        grid-template-columns: 1fr;
        gap: 15px;
    }

    .footer-section {
        flex-wrap: wrap;
        gap: 10px;
    }

    .footer-bottom {
        flex-direction: column;
        text-align: center;
        gap: 10px;
    }

    .footer-badges {
        justify-content: center;
        gap: 8px;
    }
}

/* Modern Footer Styling - Legacy */
.modern-footer {
    background: linear-gradient(135deg, #0f0f23 0%, #1a1a2e 50%, #2d2d2d 100%);
    color: #ffffff;
    margin-top: 50px;
    position: relative;
    overflow: hidden;
    animation: fadeInUp 0.8s ease-out;
}

.modern-footer::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent 0%, #37003c 20%, #00ff87 50%, #37003c 80%, transparent 100%);
    opacity: 0.6;
    animation: shimmer 3s ease-in-out infinite;
}

.modern-footer::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(ellipse at 20% 50%, rgba(55, 0, 60, 0.1) 0%, transparent 50%),
                radial-gradient(ellipse at 80% 20%, rgba(0, 255, 135, 0.05) 0%, transparent 50%);
    pointer-events: none;
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes shimmer {
    0%, 100% {
        opacity: 0.6;
    }
    50% {
        opacity: 1;
    }
}

