        font-size: 0.75rem;
        padding: 6px 12px;
    }

    /* Mobile Legacy Footer */
    .site-footer {
        padding: 20px 10px;
    }

    .footer {
        padding: 20px 10px;
    }

    .footer-content {
        flex-direction: column;
        text-align: center;
        gap: 15px;
    }

    .footer p {
        font-size: 0.85rem;
    }

    .footer nav ul {
        justify-content: center;
        gap: 10px;
    }

    .footer nav ul li a {
