}

/* Mobile Sidebar Menu */
.mobile-sidebar {
    position: fixed;
    top: 0;
    right: -100%;
    width: 75%;
    max-width: 300px;
    height: 100vh;
    background: #ffffff;
    box-shadow: -2px 0 10px rgba(0, 0, 0, 0.1);
    z-index: 1000;
    transition: right 0.3s ease;
    overflow-y: auto;
    -webkit-overflow-scrolling: touch;
}

.mobile-sidebar.active {
    right: 0;
}

.mobile-sidebar-header {
    padding: 15px;
    border-bottom: 1px solid #e0e0e0;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: #37003c;
    position: sticky;
    top: 0;
    z-index: 10;
}

.mobile-sidebar-header h3 {
    margin: 0;
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 1.1rem;
    color: white;
    font-weight: 600;
}

.close-sidebar {
    background: rgba(255,255,255,0.2);
    border: none;
    font-size: 1.8rem;
    cursor: pointer;
    color: white;
    width: 35px;
    height: 35px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s ease;
}

.close-sidebar:hover {
    background: rgba(255,255,255,0.3);
    transform: rotate(90deg);
}

.mobile-sidebar-nav {
    padding: 20px 0;
}

.mobile-nav-links {
    list-style: none;
    margin: 0;
    padding: 0;
}

.mobile-nav-links li {
    margin: 0;
}

.mobile-nav-links a {
    display: flex;
    align-items: center;
    padding: 16px 20px;
    color: #333;
    text-decoration: none;
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 0.95rem;
    border-bottom: 1px solid #f0f0f0;
    transition: all 0.2s ease;
    gap: 10px;
}

.mobile-nav-links a:hover,
.mobile-nav-links a:active {
    background: #f8f9fa;
    color: #37003c;
    padding-left: 25px;
    border-left: 3px solid #37003c;
}

.nav-divider {
    height: 1px;
    background: #e0e0e0;
    margin: 10px 0;
}

.mobile-sidebar-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    background: rgba(0, 0, 0, 0.5);
    z-index: 999;
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s ease;
}

.mobile-sidebar-overlay.active {
    opacity: 1;
    visibility: visible;
}

/* Additional Mobile Optimizations */
@media (max-width: 768px) {
    /* Touch-friendly elements */
    button,
    a,
    input,
    select,
    textarea {
        min-height: 44px;
        min-width: 44px;
    }

    /* Improved tap targets */
    .nav-links a,
    .mobile-nav-links a {
        padding: 12px;
    }

    /* Grid and Layout */
    .grid-container {
        grid-template-columns: 1fr;
        gap: 20px;
    }

    .secondary-stories {
        grid-template-columns: 1fr;
        gap: 15px;
    }

    /* Forms on Mobile */
    input[type="text"],
    input[type="email"],
    input[type="password"],
    textarea {
        font-size: 16px;
        padding: 12px;
        width: 100%;
    }

    /* Buttons on Mobile */
    button,
    .btn {
        padding: 12px 20px;
        font-size: 0.95rem;
        border-radius: 6px;
        width: 100%;
        max-width: 300px;
        margin: 0 auto;
    }

    /* Mobile Article Styles */
    .nyt-article {
        max-width: 100%;
        padding: 15px 10px;
    }

    .nyt-article h1 {
        font-size: 1.6rem;
        line-height: 1.2;
        margin-bottom: 12px;
    }

    .nyt-article h2 {
        font-size: 1.3rem;
        margin: 20px 0 12px;
    }

    .nyt-article p {
        font-size: 1rem;
        line-height: 1.6;
        text-align: left;
        margin-bottom: 15px;
    }

    .nyt-article ul li {
        font-size: 0.95rem;
        margin-bottom: 8px;
    }

    .nyt-article .byline {
        font-size: 0.85rem;
        margin-bottom: 15px;
    }


    /* Mobile Performance Optimizations */
    * {
        -webkit-tap-highlight-color: transparent;
    }

    img {
        max-width: 100%;
        height: auto;
    }

    /* Smooth scrolling on iOS */
    body {
        -webkit-overflow-scrolling: touch;
    }
}

/* Small Mobile Devices (max-width: 480px) */
@media (max-width: 480px) {
    .logo h1 {
        font-size: 1.1rem;
