}

.newsletter-btn:hover {
    transform: translateY(-1px);
}

/* Mobile Article Pages */
@media (max-width: 768px) {
    .article-container {
        display: flex;
        flex-direction: column;
        padding: 0;
    }

    .article-main {
        order: 2;
        padding: 15px 10px;
    }

    .article-sidebar {
        position: static;
        order: 1;
        padding: 15px;
        margin: 0 10px 15px;
        border-radius: 8px;
    }

    .sidebar-section {
        margin-bottom: 1.5rem;
    }

    .sidebar-title {
        font-size: 1rem;
        margin-bottom: 0.8rem;
    }

    .article-content {
        padding: 0;
    }

    .article-content h1 {
        font-size: 1.6rem;
        line-height: 1.2;
        margin-bottom: 0.8rem;
    }

    .article-content h2 {
        font-size: 1.3rem;
        margin: 1.5rem 0 0.8rem;
    }

    .article-content h3 {
        font-size: 1.1rem;
        margin: 1.2rem 0 0.6rem;
    }

    .article-content p {
        font-size: 1rem;
        line-height: 1.6;
        margin-bottom: 1rem;
    }

    .byline {
        font-size: 0.85rem;
        margin-bottom: 1.5rem;
        padding-bottom: 0.8rem;
    }

    /* Mobile Related Articles */
    .related-widget {
        border-radius: 6px;
    }

    .related-item {
        padding: 0.8rem;
    }

    .related-title {
        font-size: 0.85rem;
        margin-bottom: 0.4rem;
    }

    .related-meta {
        font-size: 0.75rem;
    }

    /* Mobile Share Widget */
    .share-widget {
        padding: 1rem;
        border-radius: 6px;
    }

    .share-buttons {
        gap: 0.4rem;
    }

    .share-btn {
        width: 36px;
        height: 36px;
        font-size: 1rem;
    }

    /* Mobile Newsletter Widget */
    .newsletter-widget {
        padding: 1rem;
        border-radius: 6px;
    }

    .newsletter-widget h3 {
        font-size: 1rem;
        margin-bottom: 0.4rem;
    }

    .newsletter-widget p {
        font-size: 0.85rem;
        margin-bottom: 0.8rem;
    }

    .newsletter-input,
    .newsletter-btn {
        padding: 10px;
        font-size: 0.95rem;
    }
}
/* Premium Link Styling */
.premium-link {
    background: linear-gradient(135deg, #ff6b35, #f7931e) !important;
    color: white !important;
    padding: 8px 16px !important;
    border-radius: 20px !important;
    font-weight: 600 !important;
    text-decoration: none !important;
    transition: all 0.3s ease !important;
    border: 2px solid transparent !important;
}

.premium-link:hover {
    transform: translateY(-1px) !important;
    box-shadow: 0 4px 12px rgba(255,107,53,0.3) !important;
    background: linear-gradient(135deg, #f7931e, #ff6b35) !important;
}

