/* Slim Footer Styling */
.slim-footer {
    background: linear-gradient(135deg, #37003c 0%, #1a1a2e 100%);
    color: #ffffff;
    margin-top: 30px;
    position: relative;
    width: 100vw;
    margin-left: calc(-50vw + 50%);
    padding: 12px 0 8px 0;
}

.slim-footer .footer-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

.footer-header {
    text-align: center;
    margin-bottom: 8px;
}

.footer-header h3 {
    font-size: 1.3rem;
    font-weight: 800;
    margin: 0 0 3px 0;
    background: linear-gradient(135deg, #ffffff 0%, #00ff87 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.footer-header p {
    font-size: 0.8rem;
    color: #ccc;
    margin: 0;
    font-weight: 400;
}

.footer-nav {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 15px;
}

.footer-links {
    display: flex;
    gap: 25px;
    flex-wrap: wrap;
    justify-content: center;
    flex: 1;
}

.footer-links a {
    color: #e1e1e1;
    text-decoration: none;
    font-size: 0.9rem;
    font-weight: 600;
    transition: all 0.3s ease;
    padding: 8px 14px;
    border-radius: 20px;
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(255, 255, 255, 0.1);
    position: relative;
    overflow: hidden;
}

.footer-links a::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0, 255, 135, 0.2), transparent);
    transition: left 0.5s ease;
}

.footer-links a:hover::before {
    left: 100%;
}

.footer-links a:hover {
    color: #00ff87;
    background: rgba(0, 255, 135, 0.15);
    border-color: rgba(0, 255, 135, 0.4);
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(0, 255, 135, 0.2);
}

.footer-meta {
    display: flex;
    align-items: center;
    gap: 15px;
}

.footer-social {
    display: flex;
    gap: 8px;
}

.footer-social a {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 28px;
    height: 28px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    color: #ccc;
    text-decoration: none;
    transition: all 0.3s ease;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.footer-social a:hover {
    background: rgba(0, 255, 135, 0.2);
    color: #00ff87;
    border-color: #00ff87;
    transform: translateY(-1px);
}

.copyright {
    font-size: 0.75rem;
    color: #999;
    font-weight: 400;
}

/* Mobile responsive for slim footer */
@media (max-width: 768px) {
    .footer-nav {
        flex-direction: column;
        text-align: center;
