/* Additional Mobile Optimizations */
@media (max-width: 768px) {
    /* Touch-friendly elements */
    button,
    a,
    input,
    select,
    textarea {
        min-height: 44px;
        min-width: 44px;
    }

    /* Improved tap targets */
    .nav-links a,
    .mobile-nav-links a {
        padding: 12px;
    }

    /* Grid and Layout */
    .grid-container {
        grid-template-columns: 1fr;
        gap: 20px;
    }

    .secondary-stories {
        grid-template-columns: 1fr;
        gap: 15px;
    }

    /* Forms on Mobile */
    input[type="text"],
    input[type="email"],
    input[type="password"],
    textarea {
        font-size: 16px;
        padding: 12px;
        width: 100%;
    }

    /* Buttons on Mobile */
    button,
