}

/* Basic Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body, html {
    font-family: 'Georgia', 'Times New Roman', serif;
    background-color: #ffffff;
    color: #121212;
    line-height: 1.6;
    font-size: 16px;
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
    font-family: 'Helvetica Neue', 'Arial', sans-serif;
    font-weight: 700;
    line-height: 1.2;
    color: #121212;
}

p {
    font-family: 'Georgia', serif;
    line-height: 1.6;
    margin-bottom: 1rem;
}

/* Header */
.site-header {
    background-color: #ffffff;
    border-bottom: 1px solid #e2e2e2;
}

.header-content {
    max-width: 1000px;
    margin: 0 auto;
    padding: 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 40px;
}

.logo {
    text-align: center;
    display: flex;
    align-items: center;
    gap: 15px;
}

.logo-image {
    width: 90px;
    height: 90px;
    border-radius: 8px;
    object-fit: cover;
}

.logo-text {
    display: flex;
    flex-direction: column;
}

/* Hide auto-placed ads in unwanted locations */
.site-header + ins[class*="adsbygoogle"],
.site-header + div[id*="google_ads"],
header + ins[class*="adsbygoogle"],
header + div[id*="google_ads"],
.main-nav + ins[class*="adsbygoogle"],
.main-nav + div[id*="google_ads"],
.newsletter-banner::before ins[class*="adsbygoogle"],
body > ins[class*="adsbygoogle"]:first-of-type {
    display: none !important;
    visibility: hidden !important;
    height: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
}

/* Hide any Google ads that appear at the top of the page */
body > div[id*="google_ads"]:first-child,
body > div[id*="google_ads"]:nth-child(2),
body > ins[class*="adsbygoogle"]:first-child,
body > ins[class*="adsbygoogle"]:nth-child(2) {
    display: none !important;
}

.logo h1 {
    font-family: 'Old English Text MT', 'Times New Roman', serif;
    font-size: 2.5rem;
    font-weight: 900;
    color: #121212;
    margin: 0;
}

.tagline {
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 0.85rem;
    color: #666;
    margin-top: 2px;
}

/* Search Container */
.search-container {
    display: flex;
    align-items: center;
    gap: 5px;
}

#search-input {
    padding: 8px 12px;
    border: 1px solid #e2e2e2;
    font-size: 0.9rem;
    width: 250px;
    outline: none;
    font-family: 'Helvetica Neue', sans-serif;
}

.search-btn, .menu-btn {
    background: none;
    border: none;
    font-size: 1.2rem;
    cursor: pointer;
    padding: 8px;
    width: 36px;
    height: 36px;
    display: flex;
    align-items: center;
    justify-content: center;
}


/* Navigation */
.main-nav {
    background-color: #ffffff;
    padding: 8px 0;
}

.nav-content {
    max-width: 1000px;
    margin: 0 auto;
    padding: 0 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 40px;
}

.nav-links {
    list-style: none;
    display: flex;
    gap: 20px;
    margin: 0;
    padding: 0;
}

.nav-links a {
    color: #121212;
    text-decoration: none;
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 0.9rem;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    padding: 8px 0;
}

.nav-links a.main-nav-auth {
    color: #121212;
    text-decoration: none;
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 0.9rem;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    padding: 8px 0;
    transition: color 0.3s ease;
}

.nav-links a.main-nav-auth:hover {
    color: #37003c;
}

/* Authentication Links */
.nav-auth-links {
    list-style: none;
    display: flex;
    gap: 15px;
    margin: 0;
    padding: 0;
    align-items: center;
}

.nav-auth-links li {
    margin: 0;
    padding: 0;
}

.auth-link {
    text-decoration: none;
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 0.85rem;
    font-weight: 600;
    padding: 8px 16px;
    border-radius: 4px;
    transition: all 0.3s ease;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    display: inline-block;
    white-space: nowrap;
}

.auth-link.signin {
    color: #37003c;
    border: 1px solid #37003c;
    background: transparent;
    min-width: 80px;
    text-align: center;
}

.auth-link.signin:hover {
    background: #37003c;
    color: white;
}

.auth-link.create-account {
    background: #37003c;
    color: white;
    border: 1px solid #37003c;
    min-width: 130px;
    text-align: center;
}

.auth-link.create-account:hover {
    background: #4a0050;
    border-color: #4a0050;
}

.auth-link.account {
    background: #007bff;
    color: white;
    border: 1px solid #007bff;
}

.auth-link.account:hover {
    background: #0056b3;
    border-color: #0056b3;
}

.auth-link.premium {
    background: linear-gradient(135deg, #ffd700, #ffb347);
    color: #000;
    border: 1px solid #ffd700;
    font-weight: 700;
}

.auth-link.premium:hover {
    background: linear-gradient(135deg, #ffb347, #ffd700);
    border-color: #ffb347;
    transform: translateY(-1px);
    box-shadow: 0 4px 8px rgba(0,0,0,0.2);
}

/* Main Content Layout */
.main-content {
    background-color: #ffffff;
    min-height: calc(100vh - 200px);
}

.content-wrapper {
    max-width: 1000px;
    margin: 0 auto;
    padding: 30px 20px;
}


/* Additional Stories */
.additional-stories {
    margin-top: 40px;
    padding: 30px;
    background: linear-gradient(135deg, #f8f9fa 0%, #ffffff 100%);
    border-radius: 12px;
    border: 1px solid #e9ecef;
}

.subsection-title {
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 1.4rem;
    font-weight: 700;
    color: #121212;
    margin-bottom: 20px;
    padding-bottom: 10px;
    border-bottom: 2px solid #0066cc;
}

.headlines-list {
    display: flex;
    flex-direction: column;
    gap: 15px;
}

.headline-item {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 15px;
    background: white;
    border-radius: 8px;
    transition: all 0.2s ease;
    border-left: 4px solid transparent;
}

.headline-item:hover {
    border-left-color: #0066cc;
    box-shadow: 0 3px 10px rgba(0,0,0,0.1);
    transform: translateX(5px);
}

.headline-category {
    background: #0066cc;
    color: white;
    padding: 4px 10px;
    border-radius: 12px;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    flex-shrink: 0;
}

.headline-item a {
    color: #121212;
    text-decoration: none;
    font-weight: 500;
    transition: color 0.2s ease;
}

.headline-item:hover a {
    color: #0066cc;
}

/* Trending Section */
.trending-section {
    background: linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%);
    padding: 25px;
    border-radius: 12px;
    margin-bottom: 30px;
    border: 1px solid #e9ecef;
    box-shadow: 0 2px 8px rgba(0,0,0,0.05);
}

.trending-section h3 {
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 1.2rem;
    font-weight: 700;
    color: #121212;
    margin-bottom: 20px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.trending-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.trending-list li {
    margin-bottom: 15px;
    padding-bottom: 15px;
    border-bottom: 1px solid #e2e2e2;
}

.trending-list li:last-child {
    border-bottom: none;
    margin-bottom: 0;
    padding-bottom: 0;
}

.trending-list a {
    color: #121212;
    text-decoration: none;
    font-family: 'Georgia', serif;
    font-size: 0.95rem;
    line-height: 1.4;
    transition: color 0.2s ease;
    display: flex;
    align-items: center;
    gap: 12px;
}

.trending-list a:hover {
    color: #0066cc;
}

.trending-number {
    background: #0066cc;
    color: white;
    width: 24px;
    height: 24px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 700;
    font-size: 0.8rem;
    flex-shrink: 0;
}

.trending-text {
    flex: 1;
}





/* Article Styling */
.main_headline {
    margin-bottom: 30px;
    background: linear-gradient(145deg, #ffffff 0%, #f8f9fa 100%);
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0 6px 30px rgba(0,0,0,0.1);
    transition: all 0.3s ease;
}

.main_headline:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 40px rgba(0,0,0,0.15);
}

.main_headline a {
    text-decoration: none;
    color: inherit;
    display: block;
    transition: all 0.2s ease;
}

.main_headline a:hover {
    opacity: 1;
}

.main_headline img {
    width: 100%;
    height: auto;
    max-height: 450px;
    object-fit: cover;
    display: block;
    transition: transform 0.3s ease;
    border-radius: 12px;
}

.main_headline:hover img {
    transform: scale(1.02);
}

.main_headline h1 {
    font-size: 2.4rem;
    font-weight: 800;
    line-height: 1.15;
    margin: 20px 25px 15px 25px;
    color: #121212;
    transition: color 0.2s ease;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
}

.main_headline a:hover h1 {
    color: #0066cc;
}

.main_headline p {
    font-size: 1.15rem;
    color: #555;
    line-height: 1.65;
    font-family: 'Georgia', serif;
    margin: 0 25px 25px 25px;
    padding-bottom: 5px;
}

.main_subheadline {
    margin-bottom: 25px;
    padding: 20px 0;
    border-bottom: 1px solid #f0f0f0;
}

.main_subheadline:last-child {
    border-bottom: none;
}

.main_subheadline a {
    text-decoration: none;
    color: inherit;
    display: flex;
    gap: 20px;
    align-items: flex-start;
    transition: all 0.2s ease;
}

.main_subheadline a:hover {
    opacity: 0.95;
}

.main_subheadline img {
    width: 200px;
    height: 150px;
    object-fit: cover;
    flex-shrink: 0;
    border-radius: 10px;
}

.main_subheadline h1 {
    font-size: 1.4rem;
    font-weight: 600;
    line-height: 1.3;
    margin-bottom: 10px;
    color: #121212;
    transition: color 0.2s ease;
}

.main_subheadline a:hover h1 {
    color: #0066cc;
}

.main_subheadline p {
    font-size: 0.95rem;
    color: #666;
    line-height: 1.5;
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.headlinelink a {
    color: #121212;
    text-decoration: none;
    transition: color 0.2s ease;
}

.headlinelink a:hover {
    color: #0066cc;
}

.headlinelink {
    color: #121212;
    text-decoration: none;
    display: block;
}

.headlinelink h1 {
    color: #121212;
    text-decoration: none;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
    font-weight: 800;
    letter-spacing: -0.02em;
}


/* Slim Footer Styling */
