/* Mobile Sidebar Menu */
.mobile-sidebar {
    position: fixed;
    top: 0;
    right: -100%;
    width: 75%;
    max-width: 300px;
    height: 100vh;
    background: #ffffff;
    box-shadow: -2px 0 10px rgba(0, 0, 0, 0.1);
    z-index: 1000;
    transition: right 0.3s ease;
    overflow-y: auto;
    -webkit-overflow-scrolling: touch;
}

.mobile-sidebar.active {
    right: 0;
}

.mobile-sidebar-header {
    padding: 15px;
    border-bottom: 1px solid #e0e0e0;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: #37003c;
    position: sticky;
    top: 0;
    z-index: 10;
}

.mobile-sidebar-header h3 {
    margin: 0;
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 1.1rem;
    color: white;
    font-weight: 600;
}

.close-sidebar {
    background: rgba(255,255,255,0.2);
    border: none;
    font-size: 1.8rem;
    cursor: pointer;
    color: white;
    width: 35px;
    height: 35px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s ease;
}

.close-sidebar:hover {
    background: rgba(255,255,255,0.3);
    transform: rotate(90deg);
}

.mobile-sidebar-nav {
    padding: 20px 0;
}

.mobile-nav-links {
    list-style: none;
    margin: 0;
    padding: 0;
}

.mobile-nav-links li {
    margin: 0;
}

.mobile-nav-links a {
    display: flex;
    align-items: center;
    padding: 16px 20px;
    color: #333;
    text-decoration: none;
    font-family: 'Helvetica Neue', sans-serif;
    font-size: 0.95rem;
    border-bottom: 1px solid #f0f0f0;
    transition: all 0.2s ease;
    gap: 10px;
}

.mobile-nav-links a:hover,
.mobile-nav-links a:active {
    background: #f8f9fa;
    color: #37003c;
    padding-left: 25px;
