    margin: 10px 0;
}

.mobile-sidebar-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    background: rgba(0, 0, 0, 0.5);
    z-index: 999;
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s ease;
}

.mobile-sidebar-overlay.active {
    opacity: 1;
    visibility: visible;
}

/* Additional Mobile Optimizations */
@media (max-width: 768px) {
    /* Touch-friendly elements */
    button,
    a,
    input,
    select,
    textarea {
        min-height: 44px;
        min-width: 44px;
    }

    /* Improved tap targets */
    .nav-links a,
    .mobile-nav-links a {
        padding: 12px;
    }

    /* Grid and Layout */
    .grid-container {
        grid-template-columns: 1fr;
        gap: 20px;
    }

    .secondary-stories {
        grid-template-columns: 1fr;
        gap: 15px;
    }

    /* Forms on Mobile */
    input[type="text"],
    input[type="email"],
    input[type="password"],
    textarea {
        font-size: 16px;
        padding: 12px;
        width: 100%;
    }

    /* Buttons on Mobile */
    button,
    .btn {
        padding: 12px 20px;
        font-size: 0.95rem;
        border-radius: 6px;
        width: 100%;
        max-width: 300px;
        margin: 0 auto;
    }

    /* Mobile Article Styles */
    .nyt-article {
        max-width: 100%;
        padding: 15px 10px;
    }

    .nyt-article h1 {
        font-size: 1.6rem;
        line-height: 1.2;
        margin-bottom: 12px;
    }

    .nyt-article h2 {
        font-size: 1.3rem;
        margin: 20px 0 12px;
    }

    .nyt-article p {
        font-size: 1rem;
        line-height: 1.6;
        text-align: left;
        margin-bottom: 15px;
    }

    .nyt-article ul li {
        font-size: 0.95rem;
        margin-bottom: 8px;
    }

    .nyt-article .byline {
        font-size: 0.85rem;
        margin-bottom: 15px;
    }


    /* Mobile Performance Optimizations */
    * {
        -webkit-tap-highlight-color: transparent;
    }

    img {
        max-width: 100%;
        height: auto;
    }

    /* Smooth scrolling on iOS */
    body {
        -webkit-overflow-scrolling: touch;
    }
}

