// Enhanced FPL AI Assistant with Advanced Features
class EnhancedFPLAssistant {
    constructor() {
        this.config = {
            version: '2.0',
            features: {
                voice: true,
                visualizations: true,
                realtime: true,
                memory: true,
                export: true,
                predictions: true,
                personalization: true
            }
        };

        this.userProfile = this.loadUserProfile();
        this.conversationHistory = [];
        this.voiceEnabled = false;
        this.speechSynthesis = window.speechSynthesis;
        this.speechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
        this.charts = {};
        this.realTimeData = {};
        this.predictions = {};

        this.init();
    }

    init() {
        this.setupVoiceControls();
        this.loadConversationHistory();
        this.initializeCharts();
        this.startRealTimeUpdates();
        this.loadPredictionModels();
        this.setupExportFeatures();
        this.enhanceUI();
    }

    // Voice Input/Output Features
    setupVoiceControls() {
        if (this.speechRecognition) {
            this.recognition = new this.speechRecognition();
            this.recognition.continuous = false;
            this.recognition.interimResults = true;
            this.recognition.lang = 'en-US';

            this.recognition.onresult = (event) => {
                const transcript = Array.from(event.results)
                    .map(result => result[0].transcript)
                    .join('');

                if (event.results[0].isFinal) {
                    this.processVoiceInput(transcript);
                } else {
                    this.updateVoicePreview(transcript);
                }
            };

            this.recognition.onerror = (event) => {
                console.error('Voice recognition error:', event.error);
                this.showNotification('Voice recognition error. Please try again.', 'error');
            };
        }

        this.addVoiceButton();
    }

    addVoiceButton() {
        const voiceBtn = document.createElement('button');
        voiceBtn.className = 'voice-input-btn';
        voiceBtn.innerHTML = `
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                <path d="M12 14.5C13.66 14.5 15 13.16 15 11.5V6.5C15 4.84 13.66 3.5 12 3.5C10.34 3.5 9 4.84 9 6.5V11.5C9 13.16 10.34 14.5 12 14.5Z" stroke="currentColor" stroke-width="2"/>
                <path d="M19 11.5C19 15.09 16.09 18 12.5 18C8.91 18 6 15.09 6 11.5" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
                <path d="M12 18V21" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
            </svg>
            <span class="voice-status">Click to speak</span>
        `;

        voiceBtn.addEventListener('click', () => this.toggleVoiceInput());

        const inputContainer = document.querySelector('.chat-input-container');
        if (inputContainer) {
            inputContainer.insertBefore(voiceBtn, inputContainer.firstChild);
        }
    }

    toggleVoiceInput() {
        if (!this.recognition) {
            this.showNotification('Voice input not supported in your browser', 'warning');
            return;
        }

        if (this.voiceEnabled) {
            this.recognition.stop();
            this.voiceEnabled = false;
            this.updateVoiceButton(false);
        } else {
            this.recognition.start();
            this.voiceEnabled = true;
            this.updateVoiceButton(true);
        }
    }

    updateVoiceButton(isActive) {
        const voiceBtn = document.querySelector('.voice-input-btn');
        const status = voiceBtn.querySelector('.voice-status');

        if (isActive) {
            voiceBtn.classList.add('active');
            status.textContent = 'Listening...';
            this.animateVoiceButton();
        } else {
            voiceBtn.classList.remove('active');
            status.textContent = 'Click to speak';
        }
    }

    animateVoiceButton() {
        const voiceBtn = document.querySelector('.voice-input-btn');
        if (this.voiceEnabled && voiceBtn) {
            voiceBtn.style.animation = 'pulse 1.5s infinite';
            setTimeout(() => this.animateVoiceButton(), 100);
        } else {
            voiceBtn.style.animation = '';
        }
    }

    processVoiceInput(text) {
        const input = document.getElementById('chat-input');
        if (input) {
            input.value = text;
            this.sendMessage(text);
        }
    }

    updateVoicePreview(text) {
        const input = document.getElementById('chat-input');
        if (input) {
            input.value = text;
            input.style.fontStyle = 'italic';
        }
    }

    speakResponse(text) {
        if (!this.speechSynthesis) return;

        // Cancel any ongoing speech
        this.speechSynthesis.cancel();

        const utterance = new SpeechSynthesisUtterance(text);
        utterance.rate = 1.0;
        utterance.pitch = 1.0;
        utterance.volume = 0.8;

        // Select a natural voice
        const voices = this.speechSynthesis.getVoices();
        const preferredVoice = voices.find(voice => 
            voice.lang === 'en-GB' && voice.name.includes('Google')
        ) || voices[0];

        if (preferredVoice) {
            utterance.voice = preferredVoice;
        }

        this.speechSynthesis.speak(utterance);
    }

    // Conversation Memory & Context
    loadConversationHistory() {
        const stored = localStorage.getItem('fpl_conversation_history');
        if (stored) {
            this.conversationHistory = JSON.parse(stored);
            this.displayConversationSummary();
        }
    }

    saveConversation(message, response) {
        const entry = {
            timestamp: new Date().toISOString(),
            message,
            response,
            context: this.getCurrentContext()
        };

        this.conversationHistory.push(entry);

        // Keep only last 50 conversations
        if (this.conversationHistory.length > 50) {
            this.conversationHistory = this.conversationHistory.slice(-50);
        }

        localStorage.setItem('fpl_conversation_history', JSON.stringify(this.conversationHistory));
    }

    getCurrentContext() {
        return {
            gameweek: this.getCurrentGameweek(),
            userTeam: this.userProfile?.team || null,
            recentTopics: this.getRecentTopics(),
            preferences: this.userProfile?.preferences || {}
        };
    }

    getRecentTopics() {
        const topics = [];
        const recent = this.conversationHistory.slice(-5);

        recent.forEach(conv => {
            // Extract player names, teams, and key terms
            const players = this.extractPlayers(conv.message + ' ' + conv.response);
            const terms = this.extractKeyTerms(conv.message);
            topics.push(...players, ...terms);
        });

        return [...new Set(topics)];
    }

    extractPlayers(text) {
        const players = [];
        const playerNames = Object.keys(this.playerDatabase || {});

        playerNames.forEach(name => {
            if (text.toLowerCase().includes(name.toLowerCase())) {
                players.push(name);
            }
        });

        return players;
    }

    extractKeyTerms(text) {
        const keyTerms = ['captain', 'transfer', 'wildcard', 'bench boost', 'triple captain', 
                         'free hit', 'differential', 'template', 'price rise', 'price fall'];

        return keyTerms.filter(term => text.toLowerCase().includes(term));
    }

    displayConversationSummary() {
        const summaryDiv = document.createElement('div');
        summaryDiv.className = 'conversation-summary';
        summaryDiv.innerHTML = `
            <h4>Recent Conversations</h4>
            <div class="summary-list">
                ${this.conversationHistory.slice(-3).map(conv => `
                    <div class="summary-item">
                        <span class="summary-time">${this.formatTime(conv.timestamp)}</span>
                        <span class="summary-topic">${this.summarizeTopic(conv.message)}</span>
                    </div>
                `).join('')}
            </div>
        `;

        const chatMessages = document.getElementById('chat-messages');
        if (chatMessages && chatMessages.firstChild) {
            chatMessages.insertBefore(summaryDiv, chatMessages.firstChild.nextSibling);
        }
    }

    // Advanced Analytics & Predictions
    loadPredictionModels() {
        this.predictions = {
            playerPerformance: new PlayerPerformancePredictor(),
            priceChanges: new PriceChangePredictor(),
            captaincy: new CaptaincyPredictor(),
            teamSelection: new TeamSelectionPredictor()
        };
    }

    async getPrediction(type, params) {
        if (!this.predictions[type]) return null;

        try {
            const prediction = await this.predictions[type].predict(params);
            return this.formatPrediction(type, prediction);
        } catch (error) {
            console.error('Prediction error:', error);
            return null;
        }
    }

    formatPrediction(type, data) {
        const formatted = {
            type,
            confidence: data.confidence || 0,
            prediction: data.value,
            reasoning: data.reasoning || [],
            timestamp: new Date().toISOString()
        };

        if (data.alternatives) {
            formatted.alternatives = data.alternatives;
        }

        return formatted;
    }

    // Interactive Visualizations
    initializeCharts() {
        // Check if Chart.js is available
        if (typeof Chart === 'undefined') {
            this.loadChartLibrary();
        }
    }

    loadChartLibrary() {
        const script = document.createElement('script');
        script.src = 'https://cdn.jsdelivr.net/npm/chart.js';
        script.onload = () => {
            this.chartsReady = true;
            this.setupChartTemplates();
        };
        document.head.appendChild(script);
    }

    setupChartTemplates() {
        this.chartTemplates = {
            playerComparison: {
                type: 'radar',
                options: {
                    responsive: true,
                    plugins: {
                        legend: { position: 'top' },
                        title: { display: true, text: 'Player Comparison' }
                    }
                }
            },
            formTrend: {
                type: 'line',
                options: {
                    responsive: true,
                    plugins: {
                        legend: { position: 'top' },
                        title: { display: true, text: 'Form Trend' }
                    }
                }
            },
            teamAnalysis: {
                type: 'bar',
                options: {
                    responsive: true,
                    plugins: {
                        legend: { position: 'top' },
                        title: { display: true, text: 'Team Analysis' }
                    }
                }
            },
            fixtureHeatmap: {
                type: 'heatmap',
                options: {
                    responsive: true,
                    plugins: {
                        legend: { position: 'right' },
                        title: { display: true, text: 'Fixture Difficulty' }
                    }
                }
            }
        };
    }

    createVisualization(type, data) {
        const chartContainer = document.createElement('div');
        chartContainer.className = 'chart-container';
        chartContainer.innerHTML = `
            <canvas id="chart-${Date.now()}"></canvas>
            <div class="chart-controls">
                <button onclick="this.parentElement.parentElement.remove()">âœ• Close</button>
                <button onclick="assistant.exportChart('${type}')">ğŸ“¥ Export</button>
            </div>
        `;

        const canvas = chartContainer.querySelector('canvas');
        const ctx = canvas.getContext('2d');

        const template = this.chartTemplates[type] || this.chartTemplates.playerComparison;

        this.charts[type] = new Chart(ctx, {
            type: template.type,
            data: data,
            options: template.options
        });

        return chartContainer;
    }

    // Real-time Data Updates
    startRealTimeUpdates() {
        // WebSocket connection for real-time updates
        this.setupWebSocket();

        // Polling fallback
        this.startPolling();
    }

    setupWebSocket() {
        try {
            this.ws = new WebSocket('wss://api.eplnewshub.com/live');

            this.ws.onopen = () => {
                console.log('Real-time connection established');
                this.subscribeToUpdates();
            };

            this.ws.onmessage = (event) => {
                const data = JSON.parse(event.data);
                this.handleRealTimeUpdate(data);
            };

            this.ws.onerror = (error) => {
                console.error('WebSocket error:', error);
                this.fallbackToPolling();
            };

            this.ws.onclose = () => {
                console.log('WebSocket closed, attempting reconnect...');
                setTimeout(() => this.setupWebSocket(), 5000);
            };
        } catch (error) {
            console.error('WebSocket setup failed:', error);
            this.fallbackToPolling();
        }
    }

    subscribeToUpdates() {
        if (this.ws && this.ws.readyState === WebSocket.OPEN) {
            this.ws.send(JSON.stringify({
                action: 'subscribe',
                channels: ['live_scores', 'price_changes', 'team_news', 'injuries']
            }));
        }
    }

    handleRealTimeUpdate(data) {
        switch(data.type) {
            case 'live_score':
                this.updateLiveScore(data);
                break;
            case 'price_change':
                this.updatePriceChange(data);
                break;
            case 'team_news':
                this.updateTeamNews(data);
                break;
            case 'injury':
                this.updateInjuryNews(data);
                break;
        }

        this.notifyRelevantUpdates(data);
    }

    updateLiveScore(data) {
        const notification = document.createElement('div');
        notification.className = 'live-update live-score';
        notification.innerHTML = `
            <span class="live-badge">LIVE</span>
            <span class="update-content">
                âš½ ${data.player} scores! ${data.team} ${data.score}
            </span>
        `;

        this.displayNotification(notification);
    }

    // Export & Share Features
    setupExportFeatures() {
        this.exportFormats = ['PDF', 'CSV', 'JSON', 'Image'];
        this.addExportButtons();
    }

    addExportButtons() {
        const exportPanel = document.createElement('div');
        exportPanel.className = 'export-panel';
        exportPanel.innerHTML = `
            <button class="export-btn" onclick="assistant.exportConversation()">
                ğŸ“¥ Export Chat
            </button>
            <button class="share-btn" onclick="assistant.shareInsights()">
                ğŸ”— Share Insights
            </button>
        `;

        const chatInterface = document.querySelector('.chat-interface');
        if (chatInterface) {
            chatInterface.appendChild(exportPanel);
        }
    }

    exportConversation(format = 'PDF') {
        const data = this.prepareExportData();

        switch(format) {
            case 'PDF':
                this.exportAsPDF(data);
                break;
            case 'CSV':
                this.exportAsCSV(data);
                break;
            case 'JSON':
                this.exportAsJSON(data);
                break;
            case 'Image':
                this.exportAsImage();
                break;
        }
    }

    prepareExportData() {
        return {
            timestamp: new Date().toISOString(),
            conversations: this.conversationHistory,
            insights: this.generateInsights(),
            recommendations: this.getCurrentRecommendations(),
            userProfile: this.sanitizeUserProfile()
        };
    }

    exportAsPDF(data) {
        // Create PDF content
        const content = `
            <h1>FPL AI Assistant Report</h1>
            <p>Generated: ${new Date().toLocaleDateString()}</p>

            <h2>Key Insights</h2>
            ${data.insights.map(i => `<p>â€¢ ${i}</p>`).join('')}

            <h2>Recommendations</h2>
            ${data.recommendations.map(r => `<p>â€¢ ${r}</p>`).join('')}

            <h2>Conversation History</h2>
            ${data.conversations.map(c => `
                <div>
                    <strong>Q:</strong> ${c.message}<br>
                    <strong>A:</strong> ${c.response}
                </div>
            `).join('')}
        `;

        // Use browser print functionality
        const printWindow = window.open('', '', 'height=600,width=800');
        printWindow.document.write('<html><head><title>FPL AI Report</title>');
        printWindow.document.write('<style>body{font-family:Arial,sans-serif;padding:20px;}</style>');
        printWindow.document.write('</head><body>');
        printWindow.document.write(content);
        printWindow.document.write('</body></html>');
        printWindow.document.close();
        printWindow.print();
    }

    shareInsights() {
        const insights = this.generateInsights();
        const shareData = {
            title: 'FPL AI Insights',
            text: insights.join('\n'),
            url: window.location.href
        };

        if (navigator.share) {
            navigator.share(shareData);
        } else {
            // Fallback to copy to clipboard
            this.copyToClipboard(shareData.text);
            this.showNotification('Insights copied to clipboard!', 'success');
        }
    }

    // Personalized Recommendations
    loadUserProfile() {
        const stored = localStorage.getItem('fpl_user_profile');
        if (stored) {
            return JSON.parse(stored);
        }

        return this.createDefaultProfile();
    }

    createDefaultProfile() {
        return {
            id: this.generateUserId(),
            created: new Date().toISOString(),
            preferences: {
                riskLevel: 'medium',
                favoriteTeams: [],
                favoritePlayers: [],
                budget: 100.0,
                strategy: 'balanced'
            },
            team: null,
            history: [],
            achievements: []
        };
    }

    updateUserProfile(updates) {
        this.userProfile = { ...this.userProfile, ...updates };
        localStorage.setItem('fpl_user_profile', JSON.stringify(this.userProfile));
        this.generatePersonalizedRecommendations();
    }

    generatePersonalizedRecommendations() {
        const recommendations = [];

        // Based on risk level
        if (this.userProfile.preferences.riskLevel === 'high') {
            recommendations.push('Consider differential captains with high ceiling');
            recommendations.push('Target players with explosive potential');
        } else if (this.userProfile.preferences.riskLevel === 'low') {
            recommendations.push('Stick with template picks for safety');
            recommendations.push('Focus on consistent performers');
        }

        // Based on favorite teams
        if (this.userProfile.preferences.favoriteTeams.length > 0) {
            const team = this.userProfile.preferences.favoriteTeams[0];
            recommendations.push(`Monitor ${team} players for potential bias`);
        }

        // Based on strategy
        switch(this.userProfile.preferences.strategy) {
            case 'aggressive':
                recommendations.push('Consider early wildcard for team optimization');
                break;
            case 'conservative':
                recommendations.push('Save transfers for injury coverage');
                break;
            case 'balanced':
                recommendations.push('Mix template and differential picks');
                break;
        }

        return recommendations;
    }

    // Enhanced UI Components
    enhanceUI() {
        this.addFloatingActionButton();
        this.addQuickStats();
        this.addThemeToggle();
        this.addAccessibilityFeatures();
    }

    addFloatingActionButton() {
        const fab = document.createElement('div');
        fab.className = 'floating-action-button';
        fab.innerHTML = `
            <button class="fab-main">
                <span>âœ¨</span>
            </button>
            <div class="fab-menu">
                <button onclick="assistant.quickAnalysis()">ğŸ” Quick Analysis</button>
                <button onclick="assistant.showTrends()">ğŸ“ˆ Trends</button>
                <button onclick="assistant.comparePlayerd()">âš–ï¸ Compare</button>
                <button onclick="assistant.showHelp()">â“ Help</button>
            </div>
        `;

        document.body.appendChild(fab);

        fab.querySelector('.fab-main').addEventListener('click', () => {
            fab.classList.toggle('active');
        });
    }

    addQuickStats() {
        const statsBar = document.createElement('div');
        statsBar.className = 'quick-stats-bar';
        statsBar.innerHTML = `
            <div class="stat-item">
                <span class="stat-label">GW</span>
                <span class="stat-value">${this.getCurrentGameweek()}</span>
            </div>
            <div class="stat-item">
                <span class="stat-label">Deadline</span>
                <span class="stat-value">${this.getNextDeadline()}</span>
            </div>
            <div class="stat-item">
                <span class="stat-label">Top Transfer</span>
                <span class="stat-value">${this.getTopTransfer()}</span>
            </div>
            <div class="stat-item">
                <span class="stat-label">Price Changes</span>
                <span class="stat-value">${this.getPriceChangeCount()}</span>
            </div>
        `;

        const container = document.querySelector('.ai-container');
        if (container) {
            container.insertBefore(statsBar, container.firstChild);
        }
    }

    addThemeToggle() {
        const toggle = document.createElement('button');
        toggle.className = 'theme-toggle';
        toggle.innerHTML = 'ğŸŒ™';
        toggle.onclick = () => this.toggleTheme();

        document.body.appendChild(toggle);
    }

    toggleTheme() {
        document.body.classList.toggle('dark-theme');
        const isDark = document.body.classList.contains('dark-theme');
        localStorage.setItem('fpl_theme', isDark ? 'dark' : 'light');

        const toggle = document.querySelector('.theme-toggle');
        toggle.innerHTML = isDark ? 'â˜€ï¸' : 'ğŸŒ™';
    }

    addAccessibilityFeatures() {
        // Keyboard shortcuts
        document.addEventListener('keydown', (e) => {
            // Ctrl/Cmd + K: Focus search
            if ((e.ctrlKey || e.metaKey) && e.key === 'k') {
                e.preventDefault();
                document.getElementById('chat-input')?.focus();
            }

            // Ctrl/Cmd + /: Show help
            if ((e.ctrlKey || e.metaKey) && e.key === '/') {
                e.preventDefault();
                this.showHelp();
            }

            // Escape: Close modals
            if (e.key === 'Escape') {
                this.closeAllModals();
            }
        });

        // Screen reader announcements
        this.announcer = document.createElement('div');
        this.announcer.className = 'sr-only';
        this.announcer.setAttribute('aria-live', 'polite');
        this.announcer.setAttribute('aria-atomic', 'true');
        document.body.appendChild(this.announcer);
    }

    announce(message) {
        if (this.announcer) {
            this.announcer.textContent = message;
            setTimeout(() => {
                this.announcer.textContent = '';
            }, 1000);
        }
    }

    // Utility Methods
    showNotification(message, type = 'info') {
        const notification = document.createElement('div');
        notification.className = `notification notification-${type}`;
        notification.innerHTML = `
            <span class="notification-icon">${this.getNotificationIcon(type)}</span>
            <span class="notification-message">${message}</span>
            <button class="notification-close" onclick="this.parentElement.remove()">âœ•</button>
        `;

        document.body.appendChild(notification);

        setTimeout(() => {
            notification.classList.add('show');
        }, 100);

        setTimeout(() => {
            notification.classList.remove('show');
            setTimeout(() => notification.remove(), 300);
        }, 5000);
    }

    getNotificationIcon(type) {
        const icons = {
            info: 'â„¹ï¸',
            success: 'âœ…',
            warning: 'âš ï¸',
            error: 'âŒ'
        };
        return icons[type] || icons.info;
    }

    getCurrentGameweek() {
        // Get from API or calculate based on date
        return 20; // Placeholder
    }

    getNextDeadline() {
        // Calculate next deadline
        return '2 days'; // Placeholder
    }

    getTopTransfer() {
        // Get from trending data
        return 'Salah'; // Placeholder
    }

    getPriceChangeCount() {
        // Get from price change data
        return '+5 -3'; // Placeholder
    }

    generateUserId() {
        return 'user_' + Math.random().toString(36).substr(2, 9);
    }

    copyToClipboard(text) {
        const textarea = document.createElement('textarea');
        textarea.value = text;
        document.body.appendChild(textarea);
        textarea.select();
        document.execCommand('copy');
        document.body.removeChild(textarea);
    }

    formatTime(timestamp) {
        const date = new Date(timestamp);
        const now = new Date();
        const diff = now - date;

        if (diff < 3600000) {
            return Math.floor(diff / 60000) + ' mins ago';
        } else if (diff < 86400000) {
            return Math.floor(diff / 3600000) + ' hours ago';
        } else {
            return date.toLocaleDateString();
        }
    }

    summarizeTopic(message) {
        if (message.length <= 50) return message;
        return message.substring(0, 47) + '...';
    }

    closeAllModals() {
        document.querySelectorAll('.modal').forEach(modal => modal.remove());
    }

    showHelp() {
        const helpModal = document.createElement('div');
        helpModal.className = 'modal help-modal';
        helpModal.innerHTML = `
            <div class="modal-content">
                <h2>FPL AI Assistant Help</h2>
                <div class="help-content">
                    <h3>Voice Commands</h3>
                    <p>Click the microphone button to use voice input</p>

                    <h3>Keyboard Shortcuts</h3>
                    <ul>
                        <li><kbd>Ctrl</kbd> + <kbd>K</kbd> - Focus chat input</li>
                        <li><kbd>Ctrl</kbd> + <kbd>/</kbd> - Show help</li>
                        <li><kbd>Esc</kbd> - Close modals</li>
                    </ul>

                    <h3>Features</h3>
                    <ul>
                        <li>Real-time updates on scores and price changes</li>
                        <li>Interactive charts and visualizations</li>
                        <li>Export conversations and insights</li>
                        <li>Personalized recommendations</li>
                        <li>Voice input and output</li>
                    </ul>
                </div>
                <button onclick="this.parentElement.parentElement.remove()">Close</button>
            </div>
        `;

        document.body.appendChild(helpModal);
    }
}

