                <span class="stat-value">${this.getNextDeadline()}</span>
            </div>
            <div class="stat-item">
                <span class="stat-label">Top Transfer</span>
                <span class="stat-value">${this.getTopTransfer()}</span>
            </div>
            <div class="stat-item">
                <span class="stat-label">Price Changes</span>
                <span class="stat-value">${this.getPriceChangeCount()}</span>
            </div>
        `;

        const container = document.querySelector('.ai-container');
        if (container) {
            container.insertBefore(statsBar, container.firstChild);
        }
    }

    addThemeToggle() {
        const toggle = document.createElement('button');
        toggle.className = 'theme-toggle';
        toggle.innerHTML = 'ğŸŒ™';
        toggle.onclick = () => this.toggleTheme();

        document.body.appendChild(toggle);
    }

    toggleTheme() {
        document.body.classList.toggle('dark-theme');
        const isDark = document.body.classList.contains('dark-theme');
        localStorage.setItem('fpl_theme', isDark ? 'dark' : 'light');

        const toggle = document.querySelector('.theme-toggle');
        toggle.innerHTML = isDark ? 'â˜€ï¸' : 'ğŸŒ™';
    }

    addAccessibilityFeatures() {
        // Keyboard shortcuts
        document.addEventListener('keydown', (e) => {
            // Ctrl/Cmd + K: Focus search
            if ((e.ctrlKey || e.metaKey) && e.key === 'k') {
                e.preventDefault();
                document.getElementById('chat-input')?.focus();
            }

            // Ctrl/Cmd + /: Show help
            if ((e.ctrlKey || e.metaKey) && e.key === '/') {
                e.preventDefault();
                this.showHelp();
            }

            // Escape: Close modals
            if (e.key === 'Escape') {
                this.closeAllModals();
            }
        });

        // Screen reader announcements
        this.announcer = document.createElement('div');
        this.announcer.className = 'sr-only';
        this.announcer.setAttribute('aria-live', 'polite');
        this.announcer.setAttribute('aria-atomic', 'true');
        document.body.appendChild(this.announcer);
    }

    announce(message) {
        if (this.announcer) {
            this.announcer.textContent = message;
            setTimeout(() => {
                this.announcer.textContent = '';
            }, 1000);
        }
    }

    // Utility Methods
    showNotification(message, type = 'info') {
        const notification = document.createElement('div');
        notification.className = `notification notification-${type}`;
        notification.innerHTML = `
            <span class="notification-icon">${this.getNotificationIcon(type)}</span>
            <span class="notification-message">${message}</span>
            <button class="notification-close" onclick="this.parentElement.remove()">âœ•</button>
        `;

        document.body.appendChild(notification);

        setTimeout(() => {
            notification.classList.add('show');
        }, 100);

        setTimeout(() => {
            notification.classList.remove('show');
            setTimeout(() => notification.remove(), 300);
        }, 5000);
    }

    getNotificationIcon(type) {
        const icons = {
            info: 'â„¹ï¸',
            success: 'âœ…',
            warning: 'âš ï¸',
            error: 'âŒ'
        };
        return icons[type] || icons.info;
    }

    getCurrentGameweek() {
        // Get from API or calculate based on date
        return 20; // Placeholder
    }

    getNextDeadline() {
        // Calculate next deadline
        return '2 days'; // Placeholder
    }

    getTopTransfer() {
        // Get from trending data
        return 'Salah'; // Placeholder
    }

    getPriceChangeCount() {
        // Get from price change data
        return '+5 -3'; // Placeholder
    }

    generateUserId() {
        return 'user_' + Math.random().toString(36).substr(2, 9);
    }

    copyToClipboard(text) {
        const textarea = document.createElement('textarea');
        textarea.value = text;
        document.body.appendChild(textarea);
        textarea.select();
        document.execCommand('copy');
        document.body.removeChild(textarea);
    }

    formatTime(timestamp) {
        const date = new Date(timestamp);
        const now = new Date();
        const diff = now - date;

        if (diff < 3600000) {
            return Math.floor(diff / 60000) + ' mins ago';
        } else if (diff < 86400000) {
            return Math.floor(diff / 3600000) + ' hours ago';
        } else {
            return date.toLocaleDateString();
        }
    }

    summarizeTopic(message) {
        if (message.length <= 50) return message;
        return message.substring(0, 47) + '...';
    }

    closeAllModals() {
        document.querySelectorAll('.modal').forEach(modal => modal.remove());
    }

    showHelp() {
        const helpModal = document.createElement('div');
        helpModal.className = 'modal help-modal';
        helpModal.innerHTML = `
