    // Enhanced Features Methods
    initEnhancedFeatures() {
        this.setupVoiceControls();
        this.loadConversationMemory();
        this.initPredictions();
        this.addEnhancedUI();
    }

    setupVoiceControls() {
        if (!this.speechRecognition) return;

        this.recognition = new this.speechRecognition();
        this.recognition.continuous = false;
        this.recognition.interimResults = true;
        this.recognition.lang = 'en-US';

        this.recognition.onresult = (event) => {
            const transcript = Array.from(event.results)
                .map(result => result[0].transcript)
                .join('');

            if (event.results[0].isFinal) {
                this.processVoiceCommand(transcript);
            }
        };

        this.recognition.onerror = (event) => {
            console.error('Voice error:', event.error);
        };
    }

    processVoiceCommand(text) {
        const input = this.sidebar?.querySelector('.sidebar-input');
        if (input) {
            input.value = text;
            this.sendMessage();
        }
    }

    toggleVoice() {
        if (!this.recognition) return;

        if (this.voiceEnabled) {
            this.recognition.stop();
            this.voiceEnabled = false;
        } else {
            this.recognition.start();
            this.voiceEnabled = true;
        }

        this.updateVoiceButton();
    }

    updateVoiceButton() {
        const voiceBtn = this.sidebar?.querySelector('.voice-btn');
        if (voiceBtn) {
            voiceBtn.classList.toggle('active', this.voiceEnabled);
            voiceBtn.innerHTML = this.voiceEnabled ? 'ğŸ¤ Listening...' : 'ğŸ¤';
        }
    }

    speakResponse(text) {
        if (!this.speechSynthesis) return;

        // Remove HTML and keep only text
        const cleanText = text.replace(/<[^>]*>/g, '').substring(0, 200);

        const utterance = new SpeechSynthesisUtterance(cleanText);
        utterance.rate = 1.0;
        utterance.pitch = 1.0;
        utterance.volume = 0.8;

        this.speechSynthesis.speak(utterance);
    }

    loadConversationMemory() {
        const stored = localStorage.getItem(CONFIG.CONVERSATION_MEMORY_KEY);
        if (stored) {
            this.conversationMemory = JSON.parse(stored);
        }
    }

    saveToMemory(message, response) {
        this.conversationMemory.push({
            timestamp: new Date().toISOString(),
            message,
            response,
            context: this.currentContext
        });

        // Keep only last 20 conversations
        if (this.conversationMemory.length > 20) {
            this.conversationMemory = this.conversationMemory.slice(-20);
        }

        localStorage.setItem(CONFIG.CONVERSATION_MEMORY_KEY, JSON.stringify(this.conversationMemory));
    }

    initPredictions() {
        // Simple prediction models
        this.predictions = {
            captaincy: (players) => {
                // Analyze player data for captain recommendation
                const candidates = Object.entries(this.playerData)
                    .filter(([name, data]) => data.form > 7)
                    .sort((a, b) => b[1].form - a[1].form)
                    .slice(0, 3);

                return candidates.map(([name, data]) => ({
                    player: name,
                    confidence: data.form / 10,
                    reasoning: `Form: ${data.form}, PPG: ${data.ppg}`
                }));
            },

            priceChanges: (player) => {
                const data = this.playerData[player];
                if (!data) return null;

                const prediction = data.ownership > 40 && data.form > 7 ? 'rise' : 
                                 data.ownership < 10 && data.form < 5 ? 'fall' : 'stable';

                return {
                    prediction,
                    confidence: 0.65,
                    reasoning: `Ownership: ${data.ownership}%, Form: ${data.form}`
                };
            }
        };
    }

    addEnhancedUI() {
        // Add voice button to input area
        const inputContainer = this.sidebar?.querySelector('.sidebar-input-container');
        if (inputContainer && this.speechRecognition) {
            const voiceBtn = document.createElement('button');
            voiceBtn.className = 'voice-btn';
            voiceBtn.innerHTML = 'ğŸ¤';
            voiceBtn.onclick = () => this.toggleVoice();
            inputContainer.appendChild(voiceBtn);
        }

        // Add quick stats panel
        const statsPanel = document.createElement('div');
        statsPanel.className = 'sidebar-stats-panel';
        statsPanel.innerHTML = `
            <div class="sidebar-stat">
                <span class="stat-label">Messages</span>
                <span class="stat-value">${this.messageCount}</span>
            </div>
            <div class="sidebar-stat">
                <span class="stat-label">Memory</span>
                <span class="stat-value">${this.conversationMemory.length}</span>
            </div>
        `;

        if (this.sidebar) {
            const header = this.sidebar.querySelector('.sidebar-header');
            if (header) {
                header.appendChild(statsPanel);
            }
        }
    }

    // Export conversation
    exportConversation() {
        const data = {
            timestamp: new Date().toISOString(),
            messages: this.messages,
            memory: this.conversationMemory,
            stats: {
                totalMessages: this.messageCount,
                userType: this.userType
            }
        };

        const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);

        const a = document.createElement('a');
        a.href = url;
        a.download = `fpl-ai-conversation-${Date.now()}.json`;
        a.click();

        URL.revokeObjectURL(url);
    }

    // Create visualization
    createChart(type, data) {
        if (typeof Chart === 'undefined') return null;

        const canvas = document.createElement('canvas');
        canvas.width = 300;
        canvas.height = 200;

        const ctx = canvas.getContext('2d');

        new Chart(ctx, {
            type: type,
            data: data,
            options: {
                responsive: true,
                maintainAspectRatio: false
            }
        });

        return canvas;
    }

    // Show player comparison chart
    showPlayerComparison(players) {
        const playerNames = players.split(',').map(p => p.trim());
        const datasets = [];

        playerNames.forEach((name, idx) => {
            const player = this.playerData[name];
            if (player) {
                datasets.push({
                    label: name,
                    data: [
                        player.goals,
                        player.assists,
                        player.points / 10,
                        player.form * 2,
                        player.ownership / 10
                    ],
                    borderColor: `hsl(${idx * 120}, 70%, 50%)`,
                    backgroundColor: `hsla(${idx * 120}, 70%, 50%, 0.2)`
                });
            }
        });

        const chartData = {
            labels: ['Goals', 'Assists', 'Points/10', 'Form x2', 'Own%/10'],
            datasets: datasets
        };

        const chart = this.createChart('radar', chartData);
        if (chart) {
            const container = document.createElement('div');
            container.className = 'chart-message';
            container.appendChild(chart);
            this.displayMessage(container.outerHTML, 'ai');
        }
    }
}

