// Advanced Features Extension for FPL AI Assistant
class AdvancedFPLFeatures {
    constructor(assistant) {
        this.assistant = assistant;
        this.init();
    }

    init() {
        this.setupTeamBuilder();
        this.setupRivalTracking();
        this.setupWeeklyReports();
        this.setupDifferentialFinder();
        this.setupSentimentAnalysis();
        this.setupFixtureTicker();
        this.setupMLPricePredictor();
        this.setupCustomAlerts();
        this.setupAdvancedAnalytics();
        this.setupSocialIntegration();
    }

    // 1. AI-Powered Team Builder with Formation Suggestions
    setupTeamBuilder() {
        this.teamBuilder = {
            formations: ['3-4-3', '3-5-2', '4-3-3', '4-4-2', '4-5-1', '5-3-2', '5-4-1'],

            buildOptimalTeam(budget = 100.0, preferences = {}) {
                const team = {
                    formation: this.selectFormation(preferences),
                    players: [],
                    totalCost: 0,
                    expectedPoints: 0,
                    bench: []
                };

                // AI algorithm to select players
                const positions = this.getPositionRequirements(team.formation);

                for (const [position, count] of Object.entries(positions)) {
                    const selectedPlayers = this.selectBestPlayers(position, count, budget - team.totalCost, preferences);
                    team.players.push(...selectedPlayers);
                    team.totalCost += selectedPlayers.reduce((sum, p) => sum + p.price, 0);
                    team.expectedPoints += selectedPlayers.reduce((sum, p) => sum + p.expectedPoints, 0);
                }

                // Select bench players
                team.bench = this.selectBenchPlayers(budget - team.totalCost);

                return team;
            },

            selectFormation(preferences) {
                // AI logic to select best formation based on preferences
                if (preferences.attackFocus) return '3-4-3';
                if (preferences.defenseFocus) return '5-4-1';
                if (preferences.balanced) return '4-4-2';
                return '3-5-2'; // Default
            },

            getPositionRequirements(formation) {
                const formations = {
                    '3-4-3': { GK: 1, DEF: 3, MID: 4, FWD: 3 },
                    '3-5-2': { GK: 1, DEF: 3, MID: 5, FWD: 2 },
                    '4-3-3': { GK: 1, DEF: 4, MID: 3, FWD: 3 },
                    '4-4-2': { GK: 1, DEF: 4, MID: 4, FWD: 2 },
                    '4-5-1': { GK: 1, DEF: 4, MID: 5, FWD: 1 },
                    '5-3-2': { GK: 1, DEF: 5, MID: 3, FWD: 2 },
                    '5-4-1': { GK: 1, DEF: 5, MID: 4, FWD: 1 }
                };
                return formations[formation];
            },

            selectBestPlayers(position, count, budget, preferences) {
                // Complex AI algorithm for player selection
                const candidates = this.getPlayersByPosition(position);
                const scored = candidates.map(player => ({
                    ...player,
                    score: this.calculatePlayerScore(player, preferences)
                }));

                scored.sort((a, b) => b.score - a.score);

                const selected = [];
                let remainingBudget = budget;

                for (const player of scored) {
                    if (selected.length < count && player.price <= remainingBudget) {
                        selected.push(player);
                        remainingBudget -= player.price;
                    }
                }

                return selected;
            },

            calculatePlayerScore(player, preferences) {
                let score = 0;

                // Base metrics
                score += player.form * 10;
                score += player.points * 0.5;
                score += (player.goals + player.assists) * 5;

                // Preference adjustments
                if (preferences.favoriteTeams?.includes(player.team)) score *= 1.2;
                if (preferences.avoidTeams?.includes(player.team)) score *= 0.8;
                if (preferences.premiumFocus && player.price > 10) score *= 1.3;
                if (preferences.budgetFocus && player.price < 6) score *= 1.2;

                // Fixture difficulty
                score *= this.getFixtureDifficultyMultiplier(player);

                return score;
            },

            getFixtureDifficultyMultiplier(player) {
                // Analyze upcoming fixtures
                const nextFixtures = this.getNextFixtures(player.team, 5);
                const avgDifficulty = nextFixtures.reduce((sum, f) => sum + f.difficulty, 0) / nextFixtures.length;
                return 2.5 - (avgDifficulty / 2); // Convert to multiplier
            },

            visualizeTeam(team) {
                const visualization = document.createElement('div');
                visualization.className = 'team-visualization';
                visualization.innerHTML = `
                    <div class="pitch">
                        <div class="formation-display">${team.formation}</div>
                        ${this.renderFormation(team)}
                    </div>
                    <div class="team-stats">
                        <div class="stat">Total Cost: Â£${team.totalCost.toFixed(1)}m</div>
                        <div class="stat">Expected Points: ${team.expectedPoints.toFixed(1)}</div>
                        <div class="stat">Team Value: ${this.calculateTeamValue(team)}</div>
                    </div>
                `;
                return visualization;
            }
        };
    }

    // 2. Mini-League Rival Tracking and Analysis
    setupRivalTracking() {
        this.rivalTracker = {
            rivals: [],

            addRival(teamId, name) {
                this.rivals.push({
                    id: teamId,
                    name: name,
                    team: null,
                    lastUpdate: null,
                    movements: []
                });
                this.updateRivalData(teamId);
            },

            async updateRivalData(teamId) {
                try {
                    const data = await this.fetchRivalTeam(teamId);
                    const rival = this.rivals.find(r => r.id === teamId);

                    if (rival) {
                        rival.team = data.team;
                        rival.lastUpdate = new Date();
                        rival.movements.push({
                            timestamp: new Date(),
                            transfers: data.transfers,
                            chips: data.chips
                        });
                    }
                } catch (error) {
                    console.error('Error updating rival:', error);
                }
            },

            analyzeRivalMoves() {
                const analysis = {
                    commonTransfers: this.findCommonTransfers(),
                    differentials: this.findRivalDifferentials(),
                    chipUsage: this.analyzeChipUsage(),
                    predictions: this.predictRivalMoves()
                };

                return analysis;
            },

            findCommonTransfers() {
                const transferCounts = {};

                this.rivals.forEach(rival => {
                    rival.movements.forEach(move => {
                        move.transfers?.forEach(transfer => {
                            const key = `${transfer.playerOut} -> ${transfer.playerIn}`;
                            transferCounts[key] = (transferCounts[key] || 0) + 1;
                        });
                    });
                });

                return Object.entries(transferCounts)
                    .sort((a, b) => b[1] - a[1])
                    .slice(0, 5)
                    .map(([transfer, count]) => ({ transfer, count }));
            },

            predictRivalMoves() {
                // AI prediction of what rivals might do next
                const predictions = [];

                this.rivals.forEach(rival => {
                    const prediction = {
                        rival: rival.name,
                        likelyTransfers: this.predictTransfers(rival),
                        chipProbability: this.predictChipUsage(rival),
                        captainChoice: this.predictCaptain(rival)
                    };
                    predictions.push(prediction);
                });

                return predictions;
            },

            createRivalDashboard() {
                const dashboard = document.createElement('div');
                dashboard.className = 'rival-dashboard';
                dashboard.innerHTML = `
                    <h3>Mini-League Rival Tracker</h3>
                    <div class="rival-grid">
                        ${this.rivals.map(rival => `
                            <div class="rival-card">
                                <h4>${rival.name}</h4>
                                <div class="rival-stats">
                                    <span>Points: ${rival.team?.totalPoints || 0}</span>
                                    <span>Rank: ${rival.team?.overallRank || '-'}</span>
                                </div>
                                <div class="rival-recent">
                                    <h5>Recent Moves</h5>
                                    ${this.renderRecentMoves(rival)}
                                </div>
                            </div>
                        `).join('')}
                    </div>
                    <div class="rival-analysis">
                        ${this.renderRivalAnalysis()}
                    </div>
                `;
                return dashboard;
            }
        };
    }

    // 3. Automated Weekly Reports and Email Summaries
    setupWeeklyReports() {
        this.weeklyReporter = {
            generateReport(gameweek) {
                const report = {
                    gameweek: gameweek,
                    timestamp: new Date(),
                    sections: {
                        performance: this.analyzeWeeklyPerformance(gameweek),
                        transfers: this.analyzeTransferSuccess(),
                        captaincy: this.analyzeCaptainChoice(),
                        miniLeague: this.analyzeMiniLeaguePosition(),
                        recommendations: this.generateWeeklyRecommendations(),
                        insights: this.generateInsights()
                    }
                };

                return report;
            },

            analyzeWeeklyPerformance(gameweek) {
                return {
                    totalPoints: 0, // Calculate from API
                    averagePoints: 0,
                    highestScorer: null,
                    lowestScorer: null,
                    benchPoints: 0,
                    captainPoints: 0
                };
            },

            formatReportHTML(report) {
                return `
                    <div class="weekly-report">
                        <header class="report-header">
                            <h1>FPL Weekly Report - GW${report.gameweek}</h1>
                            <p class="report-date">${report.timestamp.toLocaleDateString()}</p>
                        </header>

                        <section class="performance-summary">
                            <h2>ğŸ“Š Performance Summary</h2>
                            <div class="stat-grid">
                                <div class="stat-card">
                                    <span class="stat-value">${report.sections.performance.totalPoints}</span>
                                    <span class="stat-label">Total Points</span>
                                </div>
                                <div class="stat-card">
                                    <span class="stat-value">${report.sections.performance.captainPoints}</span>
                                    <span class="stat-label">Captain Points</span>
                                </div>
                                <div class="stat-card">
                                    <span class="stat-value">${report.sections.performance.benchPoints}</span>
                                    <span class="stat-label">Bench Points</span>
                                </div>
                            </div>
                        </section>

                        <section class="insights">
                            <h2>ğŸ’¡ Key Insights</h2>
                            <ul>
                                ${report.sections.insights.map(insight => 
                                    `<li>${insight}</li>`
                                ).join('')}
                            </ul>
                        </section>

                        <section class="recommendations">
                            <h2>ğŸ¯ Recommendations for Next GW</h2>
                            ${this.formatRecommendations(report.sections.recommendations)}
                        </section>
                    </div>
                `;
            },

            scheduleWeeklyEmail(email, dayOfWeek = 'Monday', time = '09:00') {
                // Set up automated email sending
                const schedule = {
                    email: email,
                    day: dayOfWeek,
                    time: time,
                    active: true
                };

                localStorage.setItem('fpl_email_schedule', JSON.stringify(schedule));

                // In production, this would connect to a backend service
                console.log(`Weekly reports scheduled for ${email} every ${dayOfWeek} at ${time}`);
            },

            sendReport(report, email) {
                // In production, this would use an email service
                const emailData = {
                    to: email,
                    subject: `FPL Weekly Report - GW${report.gameweek}`,
                    html: this.formatReportHTML(report),
                    attachments: [
                        {
                            filename: `fpl-report-gw${report.gameweek}.pdf`,
                            content: this.generatePDF(report)
                        }
                    ]
                };

                // Simulate sending
                console.log('Report sent to:', email);
                return Promise.resolve(true);
            }
        };
    }

    // 4. AI-Powered Differential Finder
    setupDifferentialFinder() {
        this.differentialFinder = {
            findDifferentials(options = {}) {
                const maxOwnership = options.maxOwnership || 10;
                const minPoints = options.minPoints || 5;
                const positions = options.positions || ['GK', 'DEF', 'MID', 'FWD'];

                const differentials = [];

                // Analyze all players
                const players = this.getAllPlayers();

                players.forEach(player => {
                    if (player.ownership < maxOwnership && 
                        player.averagePoints >= minPoints &&
                        positions.includes(player.position)) {

                        const score = this.calculateDifferentialScore(player);

                        differentials.push({
                            ...player,
                            differentialScore: score,
                            reasoning: this.generateReasoning(player, score)
                        });
                    }
                });

                return differentials.sort((a, b) => b.differentialScore - a.differentialScore);
            },

            calculateDifferentialScore(player) {
                let score = 0;

                // Form vs ownership ratio
                score += (player.form / player.ownership) * 100;

                // Fixture advantage
                score += this.getFixtureScore(player) * 20;

                // Recent performance trend
                score += this.getTrendScore(player) * 15;

                // Value for money
                score += (player.points / player.price) * 5;

                // Penalty taker bonus
                if (player.penalties) score += 10;

                // Set piece taker bonus
                if (player.setPieces) score += 8;

                return Math.round(score);
            },

            generateReasoning(player, score) {
                const reasons = [];

                if (player.form > 7) reasons.push(`Excellent form (${player.form})`);
                if (player.ownership < 5) reasons.push(`Very low ownership (${player.ownership}%)`);
                if (this.getFixtureScore(player) > 3) reasons.push('Favorable fixtures ahead');
                if (player.penalties) reasons.push('On penalties');
                if (score > 150) reasons.push('Elite differential pick');

                return reasons;
            },

            createDifferentialWidget() {
                const widget = document.createElement('div');
                widget.className = 'differential-widget';
                widget.innerHTML = `
                    <h3>ğŸ¯ Top Differentials This Week</h3>
                    <div class="differential-filters">
                        <select id="diff-position">
                            <option value="all">All Positions</option>
                            <option value="GK">Goalkeepers</option>
                            <option value="DEF">Defenders</option>
                            <option value="MID">Midfielders</option>
                            <option value="FWD">Forwards</option>
                        </select>
                        <input type="range" id="diff-ownership" min="0" max="20" value="10">
                        <span>Max Ownership: <span id="ownership-value">10%</span></span>
                    </div>
                    <div class="differential-list" id="differential-list">
                        <!-- Differentials will be loaded here -->
                    </div>
                `;

                this.attachDifferentialEvents(widget);
                return widget;
            }
        };
    }

    // 5. Sentiment Analysis for Player News
    setupSentimentAnalysis() {
        this.sentimentAnalyzer = {
            analyzeNews(text) {
                // Sentiment keywords and scores
                const positiveWords = {
                    'excellent': 3, 'outstanding': 3, 'brilliant': 3, 'fantastic': 3,
                    'good': 2, 'strong': 2, 'solid': 2, 'impressive': 2,
                    'fit': 1, 'available': 1, 'starting': 1, 'recovered': 1
                };

                const negativeWords = {
                    'injured': -3, 'suspended': -3, 'banned': -3, 'ruled out': -3,
                    'doubt': -2, 'concern': -2, 'struggling': -2, 'poor': -2,
                    'benched': -1, 'rotation': -1, 'risk': -1, 'uncertain': -1
                };

                let score = 0;
                const words = text.toLowerCase().split(/\s+/);

                words.forEach(word => {
                    if (positiveWords[word]) score += positiveWords[word];
                    if (negativeWords[word]) score += negativeWords[word];
                });

                return {
                    score: score,
                    sentiment: score > 2 ? 'positive' : score < -2 ? 'negative' : 'neutral',
                    confidence: Math.min(Math.abs(score) / 10, 1)
                };
            },

            analyzePlayerSentiment(playerName) {
                const news = this.getPlayerNews(playerName);
                const sentiments = news.map(article => ({
                    ...article,
                    sentiment: this.analyzeNews(article.content)
                }));

                const overall = {
                    player: playerName,
                    articles: sentiments.length,
                    averageSentiment: sentiments.reduce((sum, s) => sum + s.sentiment.score, 0) / sentiments.length,
                    trend: this.calculateSentimentTrend(sentiments),
                    recommendation: this.generateSentimentRecommendation(sentiments)
                };

                return overall;
            },

            calculateSentimentTrend(sentiments) {
                if (sentiments.length < 2) return 'stable';

                const recent = sentiments.slice(-3);
                const older = sentiments.slice(0, -3);

                const recentAvg = recent.reduce((sum, s) => sum + s.sentiment.score, 0) / recent.length;
                const olderAvg = older.reduce((sum, s) => sum + s.sentiment.score, 0) / older.length;

                if (recentAvg > olderAvg + 2) return 'improving';
                if (recentAvg < olderAvg - 2) return 'declining';
                return 'stable';
            },

            createSentimentDashboard() {
                const dashboard = document.createElement('div');
                dashboard.className = 'sentiment-dashboard';
                dashboard.innerHTML = `
                    <h3>ğŸ“° News Sentiment Analysis</h3>
                    <div class="sentiment-search">
                        <input type="text" placeholder="Enter player name..." id="sentiment-search">
                        <button onclick="analyzeSentiment()">Analyze</button>
                    </div>
                    <div class="sentiment-results" id="sentiment-results">
                        <!-- Results will appear here -->
                    </div>
                    <div class="sentiment-trending">
                        <h4>Trending Sentiment</h4>
                        <div class="trending-positive">
                            <h5>ğŸ“ˆ Positive Trends</h5>
                            <ul id="positive-trends"></ul>
                        </div>
                        <div class="trending-negative">
                            <h5>ğŸ“‰ Negative Trends</h5>
                            <ul id="negative-trends"></ul>
                        </div>
                    </div>
                `;
                return dashboard;
            }
        };
    }

    // 6. Fixture Ticker with Live Odds
    setupFixtureTicker() {
        this.fixtureTicker = {
            fixtures: [],
            currentIndex: 0,

            async loadFixtures() {
                // Load fixtures from API
                this.fixtures = await this.fetchUpcomingFixtures();
                this.startTicker();
            },

            startTicker() {
                setInterval(() => {
                    this.updateTicker();
                }, 5000); // Update every 5 seconds
            },

            updateTicker() {
                const ticker = document.getElementById('fixture-ticker');
                if (!ticker) return;

                const fixture = this.fixtures[this.currentIndex];

                ticker.innerHTML = `
                    <div class="fixture-item">
                        <span class="fixture-time">${fixture.kickoff}</span>
                        <span class="fixture-teams">${fixture.home} vs ${fixture.away}</span>
                        <span class="fixture-odds">
                            H: ${fixture.odds.home} | D: ${fixture.odds.draw} | A: ${fixture.odds.away}
                        </span>
                        <span class="fixture-prediction">
                            Predicted: ${fixture.prediction}
                        </span>
                    </div>
                `;

                this.currentIndex = (this.currentIndex + 1) % this.fixtures.length;
            },

            createTickerWidget() {
                const widget = document.createElement('div');
                widget.className = 'fixture-ticker-widget';
                widget.innerHTML = `
                    <div class="ticker-header">
                        <span>âš½ Upcoming Fixtures</span>
                        <button class="ticker-control" onclick="toggleTicker()">â¸ï¸</button>
                    </div>
                    <div id="fixture-ticker" class="ticker-content">
                        Loading fixtures...
                    </div>
                `;

                this.loadFixtures();
                return widget;
            }
        };
    }

    // 7. Machine Learning Price Predictor
    setupMLPricePredictor() {
        this.mlPredictor = {
            model: null,
            features: ['form', 'minutes', 'goals', 'assists', 'cleanSheets', 'bonus', 'ownership', 'transfersIn', 'transfersOut'],

            async initModel() {
                // In production, load a pre-trained TensorFlow.js model
                // For now, using a simplified prediction algorithm
                this.model = {
                    predict: (features) => this.simplePrediction(features)
                };
            },

            simplePrediction(features) {
                let priceChange = 0;

                // Ownership impact
                if (features.ownership > 50) priceChange += 0.1;
                if (features.ownership < 5) priceChange -= 0.1;

                // Transfer activity
                const netTransfers = features.transfersIn - features.transfersOut;
                priceChange += (netTransfers / 100000) * 0.1;

                // Performance impact
                if (features.form > 8) priceChange += 0.1;
                if (features.form < 3) priceChange -= 0.1;

                // Minutes played
                if (features.minutes < 60) priceChange -= 0.05;

                return {
                    predictedChange: Math.max(-0.3, Math.min(0.3, priceChange)),
                    confidence: this.calculateConfidence(features),
                    timeframe: '7 days'
                };
            },

            calculateConfidence(features) {
                // Higher confidence with more data points
                let confidence = 0.5;

                if (features.gamesPlayed > 10) confidence += 0.2;
                if (Math.abs(features.transfersIn - features.transfersOut) > 50000) confidence += 0.15;
                if (features.ownership > 20) confidence += 0.15;

                return Math.min(confidence, 0.95);
            },

            predictMultiplePlayers(players) {
                return players.map(player => ({
                    player: player.name,
                    current: player.price,
                    prediction: this.model.predict(this.extractFeatures(player)),
                    recommendation: this.generateRecommendation(player)
                }));
            },

            extractFeatures(player) {
                return {
                    form: player.form || 0,
                    minutes: player.minutes || 0,
                    goals: player.goals || 0,
                    assists: player.assists || 0,
                    cleanSheets: player.cleanSheets || 0,
                    bonus: player.bonus || 0,
                    ownership: player.ownership || 0,
                    transfersIn: player.transfersIn || 0,
                    transfersOut: player.transfersOut || 0,
                    gamesPlayed: player.gamesPlayed || 0
                };
            },

            createPredictorWidget() {
                const widget = document.createElement('div');
                widget.className = 'price-predictor-widget';
                widget.innerHTML = `
                    <h3>ğŸ’¹ ML Price Predictor</h3>
                    <div class="predictor-input">
                        <input type="text" placeholder="Enter player name..." id="price-player-search">
                        <button onclick="predictPrice()">Predict</button>
                    </div>
                    <div class="prediction-result" id="prediction-result">
                        <!-- Prediction will appear here -->
                    </div>
                    <div class="trending-predictions">
                        <h4>Top Price Rise Predictions</h4>
                        <div id="rise-predictions"></div>
                        <h4>Top Price Fall Predictions</h4>
                        <div id="fall-predictions"></div>
                    </div>
                `;

                this.initModel();
                return widget;
            }
        };
    }

    // 8. Custom Alerts and Notifications System
    setupCustomAlerts() {
        this.alertSystem = {
            alerts: [],

            createAlert(config) {
                const alert = {
                    id: Date.now(),
                    type: config.type, // 'price', 'injury', 'lineup', 'score', 'custom'
                    condition: config.condition,
                    target: config.target,
                    action: config.action,
                    active: true,
                    triggered: false,
                    created: new Date()
                };

                this.alerts.push(alert);
                this.saveAlerts();

                return alert;
            },

            checkAlerts() {
                this.alerts.forEach(alert => {
                    if (alert.active && !alert.triggered) {
                        if (this.evaluateCondition(alert)) {
                            this.triggerAlert(alert);
                        }
                    }
                });
            },

            evaluateCondition(alert) {
                switch(alert.type) {
                    case 'price':
                        return this.checkPriceCondition(alert);
                    case 'injury':
                        return this.checkInjuryCondition(alert);
                    case 'lineup':
                        return this.checkLineupCondition(alert);
                    case 'score':
                        return this.checkScoreCondition(alert);
                    case 'custom':
                        return this.evaluateCustomCondition(alert);
                    default:
                        return false;
                }
            },

            triggerAlert(alert) {
                alert.triggered = true;
                alert.triggeredAt = new Date();

                // Execute action
                switch(alert.action.type) {
                    case 'notification':
                        this.showNotification(alert.action.message);
                        break;
                    case 'email':
                        this.sendEmailAlert(alert);
                        break;
                    case 'sms':
                        this.sendSMSAlert(alert);
                        break;
                    case 'push':
                        this.sendPushNotification(alert);
                        break;
                }

                this.saveAlerts();
            },

            showNotification(message) {
                if ('Notification' in window && Notification.permission === 'granted') {
                    new Notification('FPL Alert', {
                        body: message,
                        icon: '/fpl-icon.png',
                        badge: '/fpl-badge.png',
                        vibrate: [200, 100, 200]
                    });
                }

                // Also show in-app notification
                this.displayInAppAlert(message);
            },

            createAlertManager() {
                const manager = document.createElement('div');
                manager.className = 'alert-manager';
                manager.innerHTML = `
                    <h3>ğŸ”” Alert Manager</h3>
                    <div class="alert-creator">
                        <h4>Create New Alert</h4>
                        <select id="alert-type">
                            <option value="price">Price Change</option>
                            <option value="injury">Injury Update</option>
                            <option value="lineup">Team Lineup</option>
                            <option value="score">Live Score</option>
                            <option value="custom">Custom</option>
                        </select>
                        <input type="text" id="alert-target" placeholder="Player/Team name">
                        <input type="text" id="alert-condition" placeholder="Condition (e.g., > 0.1)">
                        <select id="alert-action">
                            <option value="notification">Browser Notification</option>
                            <option value="email">Email</option>
                            <option value="push">Push Notification</option>
                        </select>
                        <button onclick="createNewAlert()">Create Alert</button>
                    </div>
                    <div class="active-alerts">
                        <h4>Active Alerts</h4>
                        <div id="alert-list">
                            ${this.renderAlertList()}
                        </div>
                    </div>
                `;
                return manager;
            },

            renderAlertList() {
                return this.alerts.map(alert => `
                    <div class="alert-item ${alert.triggered ? 'triggered' : ''}">
                        <span class="alert-type">${alert.type}</span>
                        <span class="alert-target">${alert.target}</span>
                        <span class="alert-status">${alert.triggered ? 'âœ…' : 'â°'}</span>
                        <button onclick="removeAlert(${alert.id})">Remove</button>
                    </div>
                `).join('');
            }
        };
    }

    // 9. Advanced Analytics Dashboard
    setupAdvancedAnalytics() {
        this.analytics = {
            metrics: {},

            calculateXGI(player) {
                // Expected Goals + Assists
                return (player.xG || 0) + (player.xA || 0);
            },

            calculateROI(player) {
                // Return on Investment
                return ((player.points / player.price) * 100).toFixed(1);
            },

            calculateICT(player) {
                // Influence, Creativity, Threat index
                return {
                    influence: player.influence || 0,
                    creativity: player.creativity || 0,
                    threat: player.threat || 0,
                    total: (player.influence + player.creativity + player.threat) || 0
                };
            },

            generateHeatmap(team) {
                // Position heatmap for team
                const positions = {};
                team.players.forEach(player => {
                    positions[player.position] = positions[player.position] || [];
                    positions[player.position].push(player);
                });

                return positions;
            },

            createAnalyticsDashboard() {
                const dashboard = document.createElement('div');
                dashboard.className = 'analytics-dashboard';
                dashboard.innerHTML = `
                    <h2>ğŸ“Š Advanced Analytics</h2>
                    <div class="analytics-tabs">
                        <button class="tab-btn active" data-tab="xgi">xGI Analysis</button>
                        <button class="tab-btn" data-tab="roi">ROI Rankings</button>
                        <button class="tab-btn" data-tab="ict">ICT Index</button>
                        <button class="tab-btn" data-tab="heatmap">Position Heatmap</button>
                    </div>
                    <div class="analytics-content" id="analytics-content">
                        <!-- Content loads here -->
                    </div>
                `;

                this.attachAnalyticsEvents(dashboard);
                return dashboard;
            }
        };
    }

    // 10. Social Integration
    setupSocialIntegration() {
        this.social = {
            platforms: ['twitter', 'reddit', 'discord'],

            shareTeam(team) {
                const text = `My FPL Team (GW${this.getCurrentGW()}):\n${this.formatTeamForShare(team)}`;

                if (navigator.share) {
