// Optimized and Enhanced FPL AI Assistant - Production Ready
class OptimizedFPLAssistant {
    constructor() {
        this.version = '3.0';
        this.config = {
            wsUrl: 'wss://api.eplnewshub.com/ws',
            apiUrl: 'https://api.eplnewshub.com/v2',
            cacheExpiry: 300000, // 5 minutes
            retryAttempts: 3,
            retryDelay: 1000,
            confidenceThreshold: 0.7,
            learningRate: 0.01
        };

        this.cache = new Map();
        this.connections = new Map();
        this.learningModel = null;
        this.userFeedback = [];
        this.performance = new PerformanceMonitor();

        this.init();
    }

    async init() {
        try {
            await this.initializeConnections();
            await this.loadModels();
            await this.setupCaching();
            this.initializeErrorHandling();
            this.setupPerformanceOptimization();
            this.initializeLearningSystem();
            this.setupDataValidation();
            this.enableProgressiveEnhancement();
        } catch (error) {
            console.error('Initialization error:', error);
            this.handleInitError(error);
        }
    }

    // 1. Real-time WebSocket Implementation
    async initializeConnections() {
        return new Promise((resolve, reject) => {
            try {
                // Main WebSocket connection
                this.ws = new WebSocket(this.config.wsUrl);

                this.ws.onopen = () => {
                    console.log('WebSocket connected');
                    this.subscribeToChannels();
                    this.startHeartbeat();
                    resolve();
                };

                this.ws.onmessage = (event) => {
                    this.handleRealtimeData(event.data);
                };

                this.ws.onerror = (error) => {
                    console.error('WebSocket error:', error);
                    this.reconnectWebSocket();
                };

                this.ws.onclose = () => {
                    console.log('WebSocket disconnected');
                    this.reconnectWebSocket();
                };

                // Backup polling mechanism
                this.setupPollingFallback();

            } catch (error) {
                reject(error);
            }
        });
    }

    subscribeToChannels() {
        const channels = [
            'live_scores',
            'team_news',
            'price_changes',
            'lineups',
            'injuries',
            'transfers',
            'fixtures'
        ];

        this.ws.send(JSON.stringify({
            action: 'subscribe',
            channels: channels,
            auth: this.getAuthToken()
        }));
    }

    handleRealtimeData(data) {
        try {
            const message = JSON.parse(data);

            switch(message.type) {
                case 'live_score':
                    this.updateLiveScore(message.data);
                    break;
                case 'price_change':
                    this.updatePriceData(message.data);
                    break;
                case 'team_news':
                    this.updateTeamNews(message.data);
                    break;
                case 'lineup':
                    this.updateLineup(message.data);
                    break;
                case 'injury':
                    this.updateInjuryData(message.data);
                    break;
                case 'transfer':
                    this.updateTransferData(message.data);
                    break;
                default:
                    this.handleCustomData(message);
            }

            // Update UI
            this.refreshUI(message.type);

            // Trigger relevant alerts
            this.checkAlerts(message);

        } catch (error) {
            console.error('Data handling error:', error);
        }
    }

    reconnectWebSocket() {
        if (this.reconnectAttempts < this.config.retryAttempts) {
            setTimeout(() => {
                console.log('Attempting to reconnect...');
                this.reconnectAttempts++;
                this.initializeConnections();
            }, this.config.retryDelay * this.reconnectAttempts);
        } else {
            this.fallbackToPolling();
        }
    }

    startHeartbeat() {
        this.heartbeatInterval = setInterval(() => {
            if (this.ws.readyState === WebSocket.OPEN) {
                this.ws.send(JSON.stringify({ type: 'ping' }));
            }
        }, 30000); // Every 30 seconds
    }

    // 2. AI Learning from User Feedback
    initializeLearningSystem() {
        this.learningSystem = {
            feedbackQueue: [],
            patterns: new Map(),
            preferences: new Map(),

            recordFeedback(interaction) {
                const feedback = {
                    timestamp: Date.now(),
                    query: interaction.query,
                    response: interaction.response,
                    rating: interaction.rating,
                    corrections: interaction.corrections,
                    context: interaction.context
                };

                this.feedbackQueue.push(feedback);
                this.processLearning(feedback);
            },

            processLearning(feedback) {
                // Update patterns
                this.updatePatterns(feedback);

                // Adjust preferences
                this.adjustPreferences(feedback);

                // Train model
                if (this.feedbackQueue.length >= 10) {
                    this.trainModel();
                }
            },

            updatePatterns(feedback) {
                const pattern = this.extractPattern(feedback.query);
                const current = this.patterns.get(pattern) || { count: 0, avgRating: 0 };

                current.count++;
                current.avgRating = ((current.avgRating * (current.count - 1)) + feedback.rating) / current.count;

                this.patterns.set(pattern, current);
            },

            adjustPreferences(feedback) {
                if (feedback.rating >= 4) {
                    // Good response - reinforce this pattern
                    this.reinforcePattern(feedback);
                } else if (feedback.rating <= 2) {
                    // Poor response - adjust approach
                    this.adjustApproach(feedback);
                }
            },

            async trainModel() {
                const trainingData = this.prepareTrainingData();

                try {
                    // Send to training endpoint
                    const response = await fetch(`${this.config.apiUrl}/train`, {
                        method: 'POST',
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify(trainingData)
                    });

                    if (response.ok) {
                        const result = await response.json();
                        this.updateLocalModel(result.model);
                        this.feedbackQueue = []; // Clear processed feedback
                    }
                } catch (error) {
                    console.error('Training error:', error);
                }
            },

            extractPattern(query) {
                // Extract key patterns from queries
                const patterns = [
                    /captain|cap/i,
                    /transfer|transfers/i,
                    /differential|diff/i,
                    /wildcard|wc/i,
                    /bench|boost/i,
                    /price|rise|fall/i
                ];

                for (const pattern of patterns) {
                    if (pattern.test(query)) {
                        return pattern.source;
                    }
                }

                return 'general';
            }
        };
    }

    // 3. Performance Optimization and Caching
    setupCaching() {
        this.cacheManager = {
            cache: new Map(),

            set(key, value, ttl = 300000) {
                const expiry = Date.now() + ttl;
                this.cache.set(key, { value, expiry });

                // Limit cache size
                if (this.cache.size > 100) {
                    this.cleanup();
                }
            },

            get(key) {
                const item = this.cache.get(key);

                if (!item) return null;

                if (Date.now() > item.expiry) {
                    this.cache.delete(key);
                    return null;
                }

                return item.value;
            },

            cleanup() {
                const now = Date.now();
                for (const [key, item] of this.cache.entries()) {
                    if (now > item.expiry) {
                        this.cache.delete(key);
                    }
                }
            },

            clear() {
                this.cache.clear();
            },

            preload(keys) {
                // Preload commonly used data
                keys.forEach(key => {
                    this.prefetch(key);
                });
            },

            async prefetch(key) {
                if (!this.cache.has(key)) {
                    const data = await this.fetchData(key);
                    this.set(key, data);
                }
            }
        };

        // Setup automatic cache cleanup
        setInterval(() => {
            this.cacheManager.cleanup();
        }, 60000); // Every minute
    }

    setupPerformanceOptimization() {
        // Request Animation Frame for smooth UI updates
        this.rafQueue = [];
        this.rafId = null;

        this.queueUpdate = (fn) => {
            this.rafQueue.push(fn);

            if (!this.rafId) {
                this.rafId = requestAnimationFrame(() => {
                    const queue = this.rafQueue.slice();
                    this.rafQueue = [];
                    this.rafId = null;

                    queue.forEach(fn => fn());
                });
            }
        };

        // Debouncing for search and input
        this.debounce = (fn, delay) => {
            let timeoutId;
            return (...args) => {
                clearTimeout(timeoutId);
                timeoutId = setTimeout(() => fn.apply(this, args), delay);
            };
        };

        // Throttling for scroll and resize events
        this.throttle = (fn, limit) => {
            let inThrottle;
            return (...args) => {
                if (!inThrottle) {
                    fn.apply(this, args);
                    inThrottle = true;
                    setTimeout(() => inThrottle = false, limit);
                }
            };
        };

        // Lazy loading for images and components
        this.lazyLoad = () => {
            const images = document.querySelectorAll('img[data-src]');
            const imageObserver = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        const img = entry.target;
                        img.src = img.dataset.src;
                        img.removeAttribute('data-src');
                        imageObserver.unobserve(img);
                    }
                });
            });

            images.forEach(img => imageObserver.observe(img));
        };

        // Web Workers for heavy computations
        this.setupWorkers();
    }

    setupWorkers() {
        // Create worker for heavy calculations
        const workerCode = `
            self.onmessage = function(e) {
                const { type, data } = e.data;

                switch(type) {
                    case 'calculate':
                        const result = performCalculation(data);
                        self.postMessage({ type: 'result', data: result });
                        break;
                    case 'analyze':
                        const analysis = performAnalysis(data);
                        self.postMessage({ type: 'analysis', data: analysis });
                        break;
                }
            };

            function performCalculation(data) {
                // Heavy calculation logic
                return data;
            }

            function performAnalysis(data) {
                // Complex analysis logic
                return data;
            }
        `;

        const blob = new Blob([workerCode], { type: 'application/javascript' });
        const workerUrl = URL.createObjectURL(blob);
        this.worker = new Worker(workerUrl);

        this.worker.onmessage = (e) => {
            this.handleWorkerResult(e.data);
        };
    }

    // 4. Error Handling and Fallbacks
    initializeErrorHandling() {
        // Global error handler
        window.addEventListener('error', (event) => {
            this.logError({
                message: event.message,
                source: event.filename,
                line: event.lineno,
                column: event.colno,
                error: event.error
            });

            // Prevent default error handling
            event.preventDefault();
        });

        // Promise rejection handler
        window.addEventListener('unhandledrejection', (event) => {
            this.logError({
                type: 'unhandledRejection',
                reason: event.reason,
                promise: event.promise
            });

            // Prevent default handling
            event.preventDefault();
        });

        // Network error handling
        this.setupNetworkErrorHandling();
    }

    setupNetworkErrorHandling() {
        this.networkStatus = {
            online: navigator.onLine,
            connectionType: this.getConnectionType(),

            monitor() {
                window.addEventListener('online', () => {
                    this.online = true;
                    this.reconnect();
                });

                window.addEventListener('offline', () => {
                    this.online = false;
                    this.enableOfflineMode();
                });
            },

            reconnect() {
                this.showNotification('Connection restored', 'success');
                this.syncOfflineData();
            },

            enableOfflineMode() {
                this.showNotification('Working offline', 'warning');
                this.loadOfflineData();
            }
        };

        this.networkStatus.monitor();
    }

    async fetchWithRetry(url, options = {}, retries = 3) {
        for (let i = 0; i < retries; i++) {
            try {
                const response = await fetch(url, {
                    ...options,
                    signal: AbortSignal.timeout(5000) // 5 second timeout
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                return response;
            } catch (error) {
                console.error(`Attempt ${i + 1} failed:`, error);

                if (i === retries - 1) {
                    // Last attempt failed
                    return this.handleFetchError(error, url);
                }

                // Wait before retrying
                await new Promise(resolve => setTimeout(resolve, 1000 * (i + 1)));
            }
        }
    }

    handleFetchError(error, url) {
        // Check cache for fallback data
        const cachedData = this.cacheManager.get(url);

        if (cachedData) {
            console.log('Using cached data as fallback');
            return {
                ok: true,
                json: () => Promise.resolve(cachedData),
                cached: true
            };
        }

        // Return error response
        return {
            ok: false,
            error: error.message,
            json: () => Promise.resolve({ error: 'Network error' })
        };
    }

    // 5. Multi-language Support
    setupMultiLanguage() {
        this.i18n = {
            currentLang: localStorage.getItem('fpl_language') || 'en',
            translations: {},

            async loadTranslations(lang) {
                try {
                    const response = await fetch(`/locales/${lang}.json`);
                    this.translations[lang] = await response.json();
                } catch (error) {
                    console.error('Translation loading error:', error);
                    this.translations[lang] = this.translations['en'] || {};
                }
            },

            t(key, params = {}) {
                const keys = key.split('.');
                let value = this.translations[this.currentLang];

                for (const k of keys) {
                    value = value?.[k];
                }

                if (!value) {
                    // Fallback to English
                    value = this.getEnglishTranslation(key);
                }

                // Replace parameters
                return this.replaceParams(value || key, params);
            },

            replaceParams(str, params) {
                return str.replace(/\{(\w+)\}/g, (match, key) => {
                    return params[key] || match;
                });
            },

            async setLanguage(lang) {
                if (!this.translations[lang]) {
                    await this.loadTranslations(lang);
                }

                this.currentLang = lang;
                localStorage.setItem('fpl_language', lang);
                this.updateUILanguage();
            },

            updateUILanguage() {
                document.querySelectorAll('[data-i18n]').forEach(element => {
                    const key = element.dataset.i18n;
                    element.textContent = this.t(key);
                });
            },

            getSupportedLanguages() {
                return [
                    { code: 'en', name: 'English' },
                    { code: 'es', name: 'EspaÃ±ol' },
                    { code: 'fr', name: 'FranÃ§ais' },
                    { code: 'de', name: 'Deutsch' },
                    { code: 'it', name: 'Italiano' },
                    { code: 'pt', name: 'PortuguÃªs' },
                    { code: 'nl', name: 'Nederlands' },
                    { code: 'ar', name: 'Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©' },
                    { code: 'zh', name: 'ä¸­æ–‡' },
                    { code: 'ja', name: 'æ—¥æœ¬èª' }
                ];
            }
        };

        // Load initial language
        this.i18n.loadTranslations(this.i18n.currentLang);
    }

    // 6. AI Confidence Scoring System
    calculateConfidence(prediction) {
        const factors = {
            dataQuality: this.assessDataQuality(prediction.data),
            modelCertainty: prediction.modelConfidence || 0.5,
            historicalAccuracy: this.getHistoricalAccuracy(prediction.type),
            consensusScore: this.getConsensusScore(prediction),
            recency: this.getDataRecency(prediction.data),
            sampleSize: this.getSampleSizeScore(prediction.data)
        };

        // Weighted average
        const weights = {
            dataQuality: 0.25,
            modelCertainty: 0.20,
            historicalAccuracy: 0.20,
            consensusScore: 0.15,
            recency: 0.10,
            sampleSize: 0.10
        };

        let confidence = 0;
        for (const [factor, score] of Object.entries(factors)) {
            confidence += score * weights[factor];
        }

        // Apply confidence penalties
        confidence = this.applyConfidencePenalties(confidence, prediction);

        return {
            score: Math.min(Math.max(confidence, 0), 1),
            factors: factors,
            level: this.getConfidenceLevel(confidence),
            explanation: this.explainConfidence(factors, confidence)
        };
    }

    assessDataQuality(data) {
        let quality = 1.0;

        // Check for missing data
        const missingFields = this.checkMissingFields(data);
        quality -= missingFields.length * 0.1;

        // Check data consistency
        if (!this.isDataConsistent(data)) {
            quality -= 0.2;
        }

        // Check data source reliability
        if (data.source && this.isReliableSource(data.source)) {
            quality += 0.1;
        }

        return Math.max(quality, 0);
    }

    getConfidenceLevel(score) {
        if (score >= 0.9) return 'very_high';
        if (score >= 0.75) return 'high';
        if (score >= 0.6) return 'moderate';
        if (score >= 0.4) return 'low';
        return 'very_low';
    }

    explainConfidence(factors, score) {
        const explanations = [];

        if (factors.dataQuality < 0.5) {
            explanations.push('Limited or incomplete data available');
        }

        if (factors.modelCertainty < 0.6) {
            explanations.push('Model uncertainty is high for this prediction');
        }

        if (factors.historicalAccuracy > 0.8) {
            explanations.push('Historical predictions have been accurate');
        }

        if (factors.recency < 0.5) {
            explanations.push('Data may be outdated');
        }

        if (score >= 0.8) {
            explanations.push('High confidence in this prediction');
        }

        return explanations;
    }

    // 7. Data Validation and Sanitization
    setupDataValidation() {
        this.validator = {
            rules: {
                playerName: /^[a-zA-Z\s\-'\.]+$/,
                teamId: /^\d+$/,
                price: /^\d+\.?\d*$/,
                gameweek: /^([1-9]|[1-3][0-9]|38)$/,
                formation: /^[3-5]-[3-5]-[1-3]$/
            },

            validate(data, type) {
                const rule = this.rules[type];
                if (!rule) return true;

                return rule.test(data);
            },

            sanitize(input) {
                if (typeof input !== 'string') return input;

                // Remove HTML tags
                input = input.replace(/<[^>]*>/g, '');

                // Escape special characters
                input = input.replace(/[&<>"']/g, (match) => {
                    const escapeMap = {
                        '&': '&amp;',
                        '<': '&lt;',
                        '>': '&gt;',
                        '"': '&quot;',
                        "'": '&#39;'
                    };
                    return escapeMap[match];
                });

                // Trim whitespace
                input = input.trim();

                return input;
            },

            validateRequest(request) {
                const errors = [];

                // Check required fields
                const required = ['type', 'data'];
                for (const field of required) {
                    if (!request[field]) {
                        errors.push(`Missing required field: ${field}`);
                    }
                }

                // Validate data types
                if (request.data && typeof request.data !== 'object') {
                    errors.push('Data must be an object');
                }

                // Check data size
                if (JSON.stringify(request).length > 10000) {
                    errors.push('Request too large');
                }

                return {
                    valid: errors.length === 0,
                    errors: errors
                };
            },

            validateResponse(response) {
                // Ensure response has expected structure
                if (!response || typeof response !== 'object') {
                    return { valid: false, error: 'Invalid response format' };
                }

                if (!response.success && !response.error) {
                    return { valid: false, error: 'Response missing success/error status' };
                }

                return { valid: true };
            }
        };
    }

    // 8. Progressive Enhancement
    enableProgressiveEnhancement() {
        // Check browser capabilities
        this.capabilities = {
            webGL: this.checkWebGL(),
            webWorkers: typeof Worker !== 'undefined',
            serviceWorker: 'serviceWorker' in navigator,
            webSockets: 'WebSocket' in window,
            localStorage: this.checkLocalStorage(),
            notifications: 'Notification' in window,
            speechRecognition: 'webkitSpeechRecognition' in window || 'SpeechRecognition' in window,
            geolocation: 'geolocation' in navigator,
            webRTC: this.checkWebRTC()
        };

        // Enable features based on capabilities
        this.enableFeatures();

        // Setup service worker for offline support
        if (this.capabilities.serviceWorker) {
            this.registerServiceWorker();
        }
    }

    checkWebGL() {
        try {
            const canvas = document.createElement('canvas');
            return !!canvas.getContext('webgl') || !!canvas.getContext('experimental-webgl');
        } catch (e) {
            return false;
        }
    }

    checkLocalStorage() {
        try {
            const test = '__test__';
            localStorage.setItem(test, test);
            localStorage.removeItem(test);
            return true;
        } catch (e) {
            return false;
        }
    }

    checkWebRTC() {
        return !!(navigator.mediaDevices && navigator.mediaDevices.getUserMedia);
    }

    enableFeatures() {
        // Enable advanced visualizations only if WebGL is supported
        if (this.capabilities.webGL) {
            this.enable3DVisualizations();
        }

        // Enable voice features if supported
        if (this.capabilities.speechRecognition) {
            this.enableVoiceFeatures();
        }

        // Enable offline mode if service worker is supported
        if (this.capabilities.serviceWorker) {
            this.enableOfflineMode();
        }

        // Fallback to localStorage if IndexedDB not available
        if (!this.capabilities.indexedDB && this.capabilities.localStorage) {
            this.useLocalStorageFallback();
        }
    }

    async registerServiceWorker() {
        try {
            const registration = await navigator.serviceWorker.register('/sw.js');
            console.log('Service Worker registered:', registration);

            // Check for updates
            registration.addEventListener('updatefound', () => {
                const newWorker = registration.installing;
                newWorker.addEventListener('statechange', () => {
                    if (newWorker.state === 'installed' && navigator.serviceWorker.controller) {
                        this.showUpdateNotification();
                    }
                });
            });
        } catch (error) {
            console.error('Service Worker registration failed:', error);
        }
    }

    // Performance Monitoring
    setupPerformanceMonitoring() {
        this.performance = {
            metrics: {},

            measure(name, fn) {
                const start = performance.now();
                const result = fn();
                const end = performance.now();

                this.record(name, end - start);
                return result;
            },

            async measureAsync(name, fn) {
                const start = performance.now();
                const result = await fn();
                const end = performance.now();

                this.record(name, end - start);
                return result;
            },

            record(name, duration) {
                if (!this.metrics[name]) {
                    this.metrics[name] = [];
                }

                this.metrics[name].push(duration);

                // Keep only last 100 measurements
                if (this.metrics[name].length > 100) {
                    this.metrics[name].shift();
                }

                // Log slow operations
                if (duration > 1000) {
                    console.warn(`Slow operation: ${name} took ${duration.toFixed(2)}ms`);
                }
            },

            getAverageTime(name) {
                const times = this.metrics[name];
                if (!times || times.length === 0) return 0;

                const sum = times.reduce((a, b) => a + b, 0);
                return sum / times.length;
            },

            report() {
                const report = {};

                for (const [name, times] of Object.entries(this.metrics)) {
                    report[name] = {
                        average: this.getAverageTime(name),
                        min: Math.min(...times),
                        max: Math.max(...times),
                        count: times.length
                    };
                }

                return report;
            }
        };
    }

    // Utility Functions
    getAuthToken() {
        return localStorage.getItem('fpl_auth_token') || '';
    }

    getConnectionType() {
        const connection = navigator.connection || navigator.mozConnection || navigator.webkitConnection;
        return connection ? connection.effectiveType : 'unknown';
    }

    showNotification(message, type = 'info') {
        const notification = document.createElement('div');
        notification.className = `notification notification-${type}`;
        notification.textContent = message;

        document.body.appendChild(notification);

        setTimeout(() => {
            notification.classList.add('show');
        }, 100);

        setTimeout(() => {
            notification.classList.remove('show');
            setTimeout(() => notification.remove(), 300);
        }, 3000);
    }

    logError(error) {
        // Send to error tracking service
        if (this.config.errorTracking) {
            fetch(`${this.config.apiUrl}/errors`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    error: error,
                    timestamp: Date.now(),
                    userAgent: navigator.userAgent,
                    url: window.location.href
                })
            }).catch(console.error);
        }

        // Log locally
        console.error('Error logged:', error);
    }

    refreshUI(updateType) {
        this.queueUpdate(() => {
            const event = new CustomEvent('fpl-update', {
                detail: { type: updateType }
            });
            document.dispatchEvent(event);
        });
    }
}

// Performance Monitor Class
class PerformanceMonitor {
    constructor() {
        this.observers = [];
        this.init();
    }

    init() {
        // Monitor long tasks
        if ('PerformanceObserver' in window) {
            try {
                const observer = new PerformanceObserver((list) => {
                    for (const entry of list.getEntries()) {
                        if (entry.duration > 50) {
                            console.warn('Long task detected:', entry);
                        }
                    }
                });

                observer.observe({ entryTypes: ['longtask'] });
                this.observers.push(observer);
            } catch (e) {
                console.log('Long task monitoring not supported');
            }
        }

        // Monitor FCP and LCP
        this.monitorCoreWebVitals();
    }

    monitorCoreWebVitals() {
        // First Contentful Paint
        this.observePaint('first-contentful-paint');

        // Largest Contentful Paint
        this.observeLCP();

        // First Input Delay
        this.observeFID();

        // Cumulative Layout Shift
        this.observeCLS();
    }

    observePaint(paintType) {
        const observer = new PerformanceObserver((list) => {
            for (const entry of list.getEntries()) {
                if (entry.name === paintType) {
                    console.log(`${paintType}: ${entry.startTime}ms`);
                }
            }
        });

        observer.observe({ entryTypes: ['paint'] });
    }

    observeLCP() {
        const observer = new PerformanceObserver((list) => {
            const entries = list.getEntries();
            const lastEntry = entries[entries.length - 1];
            console.log('LCP:', lastEntry.startTime);
        });

        observer.observe({ entryTypes: ['largest-contentful-paint'] });
    }

    observeFID() {
        const observer = new PerformanceObserver((list) => {
            for (const entry of list.getEntries()) {
                const fid = entry.processingStart - entry.startTime;
                console.log('FID:', fid);
            }
        });

        observer.observe({ entryTypes: ['first-input'] });
    }

    observeCLS() {
        let cls = 0;
        const observer = new PerformanceObserver((list) => {
            for (const entry of list.getEntries()) {
                if (!entry.hadRecentInput) {
                    cls += entry.value;
                }
            }
            console.log('CLS:', cls);
        });

        observer.observe({ entryTypes: ['layout-shift'] });
    }
}

// Initialize the optimized assistant
document.addEventListener('DOMContentLoaded', () => {
    window.optimizedAssistant = new OptimizedFPLAssistant();
});

// Export for use in other modules
export { OptimizedFPLAssistant, PerformanceMonitor };

