            { id: 6, name: 'Robertson', team: 'Liverpool', teamCode: 'LIV', position: 'DEF', price: 7.0, points: 170, form: 5.9, ownership: 18.2, xG: 1.2, xA: 6.3, cleanSheets: 12 },
            { id: 7, name: 'Saliba', team: 'Arsenal', teamCode: 'ARS', position: 'DEF', price: 6.0, points: 155, form: 5.2, ownership: 22.1, xG: 2.8, xA: 0.5, cleanSheets: 10 },
            { id: 8, name: 'Gabriel', team: 'Arsenal', teamCode: 'ARS', position: 'DEF', price: 5.5, points: 145, form: 4.9, ownership: 15.3, xG: 3.1, xA: 0.3, cleanSheets: 10 },
            { id: 9, name: 'Stones', team: 'Man City', teamCode: 'MCI', position: 'DEF', price: 5.5, points: 140, form: 4.7, ownership: 12.8, xG: 2.5, xA: 0.8, cleanSheets: 11 },
            { id: 10, name: 'Trippier', team: 'Newcastle', teamCode: 'NEW', position: 'DEF', price: 5.0, points: 130, form: 4.3, ownership: 14.2, xG: 0.8, xA: 5.2, cleanSheets: 8 },

            // Midfielders
            { id: 11, name: 'Salah', team: 'Liverpool', teamCode: 'LIV', position: 'MID', price: 13.0, points: 280, form: 8.9, ownership: 62.4, xG: 19.8, xA: 12.3, cleanSheets: 0 },
            { id: 12, name: 'Palmer', team: 'Chelsea', teamCode: 'CHE', position: 'MID', price: 10.5, points: 245, form: 8.1, ownership: 48.7, xG: 18.9, xA: 9.1, cleanSheets: 0 },
            { id: 13, name: 'Saka', team: 'Arsenal', teamCode: 'ARS', position: 'MID', price: 10.0, points: 225, form: 7.5, ownership: 45.6, xG: 14.2, xA: 11.8, cleanSheets: 0 },
