    filterPlayers() {
        const searchTerm = document.getElementById('player-search').value.toLowerCase();
        const position = document.querySelector('.filter-btn.active').dataset.position;
        const maxPrice = parseFloat(document.getElementById('price-slider').value);
        const team = document.getElementById('team-filter').value;

        this.filteredPlayers = this.playerDatabase.filter(player => {
            const matchesSearch = player.name.toLowerCase().includes(searchTerm) || 
                                 player.team.toLowerCase().includes(searchTerm);
            const matchesPosition = position === 'ALL' || player.position === position;
            const matchesPrice = player.price <= maxPrice;
            const matchesTeam = team === 'ALL' || player.teamCode === team;

            return matchesSearch && matchesPosition && matchesPrice && matchesTeam;
        });

        // Sort by points descending
        this.filteredPlayers.sort((a, b) => b.points - a.points);

        this.renderPlayerList();
    }

    // Update formation
    updateFormation(formation) {
        this.team.formation = formation;
        const config = this.formations[formation];

        // Update defender row
        const defRow = document.getElementById('def-row');
        defRow.innerHTML = '';
        for (let i = 0; i < config.DEF; i++) {
            const slot = this.createPositionSlot('DEF', 1 + i);
            defRow.appendChild(slot);
        }

        // Update midfielder row
        const midRow = document.getElementById('mid-row');
        midRow.innerHTML = '';
        for (let i = 0; i < config.MID; i++) {
            const slot = this.createPositionSlot('MID', 1 + config.DEF + i);
            midRow.appendChild(slot);
        }

        // Update forward row
        const fwdRow = document.getElementById('fwd-row');
        fwdRow.innerHTML = '';
        for (let i = 0; i < config.FWD; i++) {
            const slot = this.createPositionSlot('FWD', 1 + config.DEF + config.MID + i);
            fwdRow.appendChild(slot);
        }

        // Restore players in slots
        this.restorePlayersInSlots();
    }

    createPositionSlot(position, slotIndex) {
        const slot = document.createElement('div');
        slot.className = 'position-slot';
        slot.dataset.position = position;
        slot.dataset.slot = slotIndex;

        const player = this.team.players[slotIndex];
        if (player) {
            slot.classList.add('filled');
            slot.innerHTML = `
                <div class="slot-player">
                    <div class="slot-player-name">${player.name}</div>
                    <div class="slot-player-team">${player.team}</div>
                    <div class="slot-player-price">Â£${player.price}m</div>
                </div>
            `;
        } else {
            slot.innerHTML = `<div class="empty-slot">${position}</div>`;
        }

        // Add drag and drop listeners
        slot.addEventListener('dragover', (e) => this.handleDragOver(e));
        slot.addEventListener('drop', (e) => this.handleDrop(e, slotIndex));
        slot.addEventListener('dragleave', (e) => this.handleDragLeave(e));
        slot.addEventListener('click', () => this.removePlayer(slotIndex));

        return slot;
    }

    // Drag and Drop
    initializeDragAndDrop() {
        // Already initialized in createPositionSlot
    }

    handleDragStart(e, player) {
        this.draggedPlayer = player;
        e.dataTransfer.effectAllowed = 'move';
    }

    handleDragOver(e) {
        e.preventDefault();
        e.dataTransfer.dropEffect = 'move';
        e.currentTarget.classList.add('drag-over');
    }

    handleDragLeave(e) {
        e.currentTarget.classList.remove('drag-over');
    }

    handleDrop(e, slotIndex) {
        e.preventDefault();
        e.currentTarget.classList.remove('drag-over');

        if (this.draggedPlayer) {
            this.addPlayerToSlot(this.draggedPlayer, slotIndex);
        }

        this.draggedPlayer = null;
    }

    // Add player to team
    addPlayerToSlot(player, slotIndex) {
        const slot = document.querySelector(`[data-slot="${slotIndex}"]`);
        const slotPosition = slot.dataset.position;

        // Validate position
        if (slotPosition !== 'BENCH' && slotPosition !== player.position) {
            this.showTip('INVALID', `${player.name} is a ${player.position} and cannot play in ${slotPosition} position!`);
            return;
        }

        // Check if player already in team
        const existingIndex = this.team.players.findIndex(p => p && p.id === player.id);
        if (existingIndex !== -1) {
            // Move player to new slot
            this.team.players[existingIndex] = null;
        }

        // Check budget
        const oldPlayer = this.team.players[slotIndex];
        const budgetChange = player.price - (oldPlayer ? oldPlayer.price : 0);

        if (this.team.spent + budgetChange > this.team.budget) {
            this.showTip('BUDGET', `Not enough budget! Need Â£${(this.team.spent + budgetChange - this.team.budget).toFixed(1)}m more.`);
            return;
        }

        // Check max 3 players per team
        const teamCount = this.team.players.filter(p => p && p.teamCode === player.teamCode).length;
        if (teamCount >= 3 && (!oldPlayer || oldPlayer.teamCode !== player.teamCode)) {
            this.showTip('TEAM_LIMIT', `You can only have 3 players from ${player.team}!`);
            return;
        }

        // Add player
        this.team.players[slotIndex] = player;
        this.updateBudget();
        this.updateFormation(this.team.formation);
        this.renderPlayerList();
        this.analyzeTeam();
    }

    quickAddPlayer(player) {
        // Find first empty slot for player's position
        let slotIndex = -1;

        if (player.position === 'GK') {
            slotIndex = this.team.players[0] ? -1 : 0;
        } else {
            const config = this.formations[this.team.formation];
            let startIndex = 1;
            let endIndex = 1;

            if (player.position === 'DEF') {
                endIndex = startIndex + config.DEF;
            } else if (player.position === 'MID') {
                startIndex = 1 + config.DEF;
                endIndex = startIndex + config.MID;
            } else if (player.position === 'FWD') {
                startIndex = 1 + config.DEF + config.MID;
                endIndex = startIndex + config.FWD;
            }

            for (let i = startIndex; i < endIndex; i++) {
                if (!this.team.players[i]) {
                    slotIndex = i;
                    break;
                }
            }
        }

        // If no position slot, try bench
        if (slotIndex === -1) {
            for (let i = 11; i < 15; i++) {
                if (!this.team.players[i]) {
                    slotIndex = i;
                    break;
                }
            }
        }

        if (slotIndex !== -1) {
            this.addPlayerToSlot(player, slotIndex);
        } else {
            this.showTip('FULL', `No available slots for ${player.position}!`);
        }
    }

    removePlayer(slotIndex) {
        if (this.team.players[slotIndex]) {
            this.team.players[slotIndex] = null;
            this.updateBudget();
            this.updateFormation(this.team.formation);
            this.renderPlayerList();
            this.analyzeTeam();
        }
    }

    restorePlayersInSlots() {
        // Restore players after formation change
        this.team.players.forEach((player, index) => {
            if (player && index < 11) {
                const slot = document.querySelector(`[data-slot="${index}"]`);
                if (slot) {
                    slot.classList.add('filled');
                    slot.innerHTML = `
                        <div class="slot-player">
                            <div class="slot-player-name">${player.name}</div>
                            <div class="slot-player-team">${player.team}</div>
                            <div class="slot-player-price">Â£${player.price}m</div>
                        </div>
                    `;
                }
            }
        });
    }

    // Budget management
    updateBudget() {
        this.team.spent = this.team.players.reduce((sum, player) => {
            return sum + (player ? player.price : 0);
        }, 0);

        const remaining = this.team.budget - this.team.spent;
        const playerCount = this.team.players.filter(p => p).length;

        document.getElementById('budget-remaining').textContent = `Â£${remaining.toFixed(1)}m`;
        document.getElementById('budget-spent').textContent = `Â£${this.team.spent.toFixed(1)}m`;
        document.getElementById('player-count').textContent = `${playerCount}/15`;
    }

    // AI Analysis
    analyzeTeam() {
        const analysis = this.calculateTeamScore();

        // Update scores
        document.getElementById('team-score').textContent = analysis.overall;
        document.getElementById('attack-score').textContent = analysis.attack;
        document.getElementById('midfield-score').textContent = analysis.midfield;
        document.getElementById('defense-score').textContent = analysis.defense;
        document.getElementById('value-score').textContent = analysis.value;

        // Generate tips
        this.generateTips(analysis);
    }

    calculateTeamScore() {
        const players = this.team.players.filter(p => p);

        if (players.length === 0) {
            return { overall: 0, attack: 0, midfield: 0, defense: 0, value: 0 };
        }

        // Calculate position scores
        const forwards = players.filter(p => p.position === 'FWD');
        const midfielders = players.filter(p => p.position === 'MID');
        const defenders = players.filter(p => p.position === 'DEF');
        const goalkeepers = players.filter(p => p.position === 'GK');

        const attackScore = this.calculatePositionScore(forwards, 'attack');
        const midfieldScore = this.calculatePositionScore(midfielders, 'midfield');
        const defenseScore = this.calculatePositionScore([...defenders, ...goalkeepers], 'defense');

        // Calculate value score
        const totalPoints = players.reduce((sum, p) => sum + p.points, 0);
        const totalCost = players.reduce((sum, p) => sum + p.price, 0);
        const valueScore = Math.round((totalPoints / totalCost) * 10);

        // Calculate overall score
        const overall = Math.round(
            (attackScore * 0.3 + midfieldScore * 0.3 + defenseScore * 0.25 + valueScore * 0.15) *
            (players.length / 15) // Penalty for incomplete team
        );

        return {
            overall: Math.min(100, overall),
            attack: attackScore,
            midfield: midfieldScore,
            defense: defenseScore,
            value: valueScore
        };
    }

    calculatePositionScore(players, type) {
        if (players.length === 0) return 0;

        let score = 0;

        players.forEach(player => {
            // Base score from points
            score += (player.points / 10);

            // Form bonus
            score += player.form * 2;

            // Position-specific bonuses
            if (type === 'attack') {
                score += (player.xG || 0) * 1.5;
                score += (player.xA || 0) * 1;
            } else if (type === 'defense') {
                score += (player.cleanSheets || 0) * 2;
            }

            // Ownership consideration (differentials get bonus)
            if (player.ownership < 10) {
                score += 5;
            } else if (player.ownership < 20) {
                score += 2;
            }
        });

        return Math.min(100, Math.round(score / players.length));
    }

    generateTips(analysis) {
        const tips = [];
        const players = this.team.players.filter(p => p);

        // Check team completion
        if (players.length < 15) {
            tips.push({
                type: 'INCOMPLETE',
                content: `Your team needs ${15 - players.length} more players. Complete your squad for full analysis.`
            });
        }

        // Check balance
        if (analysis.attack < 50 && players.length > 10) {
            tips.push({
                type: 'WEAK_ATTACK',
                content: 'Your attack looks weak. Consider upgrading to premium forwards or attacking midfielders.'
            });
        }

        if (analysis.defense < 50 && players.length > 10) {
            tips.push({
                type: 'WEAK_DEFENSE',
                content: 'Your defense needs strengthening. Look for defenders from top teams with good clean sheet potential.'
            });
        }

        // Check for captain options
        const captainOptions = players.filter(p => p.ownership > 30);
        if (captainOptions.length === 0 && players.length > 10) {
            tips.push({
                type: 'NO_CAPTAIN',
                content: 'You lack obvious captain choices. Consider adding Salah, Haaland, or other high-ownership premiums.'
            });
        }

        // Check for differentials
        const differentials = players.filter(p => p.ownership < 10);
        if (differentials.length === 0 && players.length > 10) {
            tips.push({
                type: 'TOO_TEMPLATE',
                content: 'Your team is very template. Add some differentials to gain rank when they perform.'
            });
        }

        // Budget tips
        const remaining = this.team.budget - this.team.spent;
        if (remaining > 2 && players.length === 15) {
            tips.push({
                type: 'MONEY_ITB',
                content: `You have Â£${remaining.toFixed(1)}m left. Consider upgrading key positions.`
            });
        }

        // Team concentration
        const teamCounts = {};
        players.forEach(p => {
            teamCounts[p.teamCode] = (teamCounts[p.teamCode] || 0) + 1;
        });

        const maxTeam = Object.entries(teamCounts).sort((a, b) => b[1] - a[1])[0];
        if (maxTeam && maxTeam[1] === 3) {
            tips.push({
                type: 'TEAM_HEAVY',
                content: `You have 3 players from ${maxTeam[0]}. This could be risky if they have a bad week.`
            });
        }

        this.displayTips(tips);
    }

    displayTips(tips) {
        const tipsContainer = document.getElementById('ai-tips');

        if (tips.length === 0) {
            tips.push({
                type: 'GOOD',
                content: 'Your team looks well-balanced! Use the Deep Analysis for more detailed insights.'
            });
        }

        tipsContainer.innerHTML = tips.map(tip => `
            <div class="tip-card">
                <span class="tip-type">${tip.type.replace('_', ' ')}</span>
                <div class="tip-content">${tip.content}</div>
            </div>
        `).join('');
    }

    showTip(type, content) {
        const tip = { type, content };
        this.displayTips([tip]);
    }

    // Auto pick team
    autoPick() {
        // Clear current team
        this.team.players = new Array(15).fill(null);

        // Sort players by value (points per million)
        const sortedPlayers = [...this.playerDatabase].sort((a, b) => {
            const valueA = a.points / a.price;
            const valueB = b.points / b.price;
            return valueB - valueA;
        });

        // Pick team based on formation
        const config = this.formations[this.team.formation];
        let budget = 100;
        const pickedTeams = {};

        // Pick GK
        const gk = this.pickBestPlayer(sortedPlayers, 'GK', budget, pickedTeams);
        if (gk) {
            this.team.players[0] = gk;
            budget -= gk.price;
            pickedTeams[gk.teamCode] = 1;
        }

        // Pick defenders
        for (let i = 0; i < config.DEF; i++) {
            const def = this.pickBestPlayer(sortedPlayers, 'DEF', budget / (15 - i), pickedTeams);
            if (def) {
                this.team.players[1 + i] = def;
                budget -= def.price;
                pickedTeams[def.teamCode] = (pickedTeams[def.teamCode] || 0) + 1;
            }
        }

        // Pick midfielders
        for (let i = 0; i < config.MID; i++) {
            const mid = this.pickBestPlayer(sortedPlayers, 'MID', budget / (15 - config.DEF - i), pickedTeams);
            if (mid) {
                this.team.players[1 + config.DEF + i] = mid;
                budget -= mid.price;
                pickedTeams[mid.teamCode] = (pickedTeams[mid.teamCode] || 0) + 1;
            }
        }

        // Pick forwards
        for (let i = 0; i < config.FWD; i++) {
            const fwd = this.pickBestPlayer(sortedPlayers, 'FWD', budget / (5 - i), pickedTeams);
            if (fwd) {
                this.team.players[1 + config.DEF + config.MID + i] = fwd;
                budget -= fwd.price;
                pickedTeams[fwd.teamCode] = (pickedTeams[fwd.teamCode] || 0) + 1;
            }
        }

        // Pick bench
        for (let i = 11; i < 15; i++) {
            const benchPlayer = this.pickBestPlayer(sortedPlayers, 'ANY', budget / (15 - i), pickedTeams);
            if (benchPlayer) {
                this.team.players[i] = benchPlayer;
                budget -= benchPlayer.price;
                pickedTeams[benchPlayer.teamCode] = (pickedTeams[benchPlayer.teamCode] || 0) + 1;
            }
        }

        this.updateBudget();
        this.updateFormation(this.team.formation);
        this.renderPlayerList();
        this.analyzeTeam();
    }

    pickBestPlayer(players, position, maxPrice, pickedTeams) {
        for (const player of players) {
            // Check if already picked
            if (this.team.players.some(p => p && p.id === player.id)) continue;

            // Check position
            if (position !== 'ANY' && player.position !== position) continue;

            // Check price
            if (player.price > maxPrice) continue;

            // Check team limit
            if (pickedTeams[player.teamCode] >= 3) continue;

            return player;
        }

        return null;
    }

    // Deep analysis
    async deepAnalysis() {
        const button = document.getElementById('analyze-team');
        const spinner = button.querySelector('.loading-spinner');
        const text = button.querySelector('span:last-child');

        spinner.style.display = 'inline-block';
        text.textContent = 'Analyzing...';

        // Simulate API call
        await new Promise(resolve => setTimeout(resolve, 1500));

        const analysis = this.performDeepAnalysis();
        this.displayTips(analysis);

        spinner.style.display = 'none';
        text.textContent = 'Deep Analysis';
    }

    performDeepAnalysis() {
        const tips = [];
        const players = this.team.players.filter(p => p);

        if (players.length < 15) {
            return [{
                type: 'INCOMPLETE',
                content: 'Complete your team first for a comprehensive analysis.'
            }];
        }

        // Analyze captain choices
        const captainOptions = players
            .sort((a, b) => b.points - a.points)
            .slice(0, 3);

        tips.push({
            type: 'CAPTAIN',
            content: `Best captain options: ${captainOptions.map(p => p.name).join(', ')}. ${captainOptions[0].name} has the highest expected points.`
        });

        // Analyze weak links
        const weakLinks = players
            .filter(p => p.form < 4)
            .sort((a, b) => a.form - b.form);

        if (weakLinks.length > 0) {
            tips.push({
                type: 'WEAK_LINKS',
                content: `Consider replacing: ${weakLinks.slice(0, 3).map(p => p.name).join(', ')}. They have poor recent form.`
            });
        }

        // Analyze differentials
        const differentials = players.filter(p => p.ownership < 15 && p.form > 5);
        if (differentials.length > 0) {
            tips.push({
                type: 'DIFFERENTIALS',
                content: `Good differentials: ${differentials.map(p => p.name).join(', ')}. Low ownership with good form.`
            });
        }

        // Fixture analysis (mock)
        tips.push({
            type: 'FIXTURES',
            content: 'Next 5 gameweeks look favorable for your Liverpool and Arsenal assets. Consider captaining Salah in GW22.'
        });

        // Value analysis
        const valueStars = players.filter(p => (p.points / p.price) > 25);
        if (valueStars.length > 0) {
            tips.push({
                type: 'VALUE',
                content: `Great value picks: ${valueStars.slice(0, 3).map(p => p.name).join(', ')}. Excellent points per million.`
            });
        }

        return tips;
    }

    // AI Suggestions
    async getAISuggestions() {
        const players = this.team.players.filter(p => p);

        if (players.length === 0) {
            this.showTip('EMPTY', 'Add some players first to get AI suggestions.');
            return;
        }

        // Simulate AI thinking
        const button = document.getElementById('get-suggestions');
        button.innerHTML = '<span class="loading-spinner"></span> Thinking...';

        await new Promise(resolve => setTimeout(resolve, 1000));

        const suggestions = this.generateAISuggestions();
        this.displayTips(suggestions);

        button.innerHTML = 'Get AI Suggestions';
    }

    generateAISuggestions() {
        const suggestions = [];
        const players = this.team.players.filter(p => p);
        const remaining = this.team.budget - this.team.spent;

        // Transfer suggestions
        if (players.length === 15) {
            const worstPerformer = players.sort((a, b) => a.form - b.form)[0];
            const replacement = this.playerDatabase
                .filter(p => p.position === worstPerformer.position && 
                           p.price <= worstPerformer.price + remaining &&
                           p.form > worstPerformer.form)
                .sort((a, b) => b.form - a.form)[0];

            if (replacement) {
                suggestions.push({
                    type: 'TRANSFER',
                    content: `Transfer suggestion: ${worstPerformer.name} â†’ ${replacement.name}. Better form and fixtures ahead.`
                });
            }
        }

        // Formation suggestion
        const currentFormation = this.team.formation;
        const optimalFormation = this.suggestOptimalFormation(players);

        if (optimalFormation !== currentFormation) {
            suggestions.push({
                type: 'FORMATION',
                content: `Consider switching to ${optimalFormation}. Better suited for your player selection.`
            });
        }

        // Budget optimization
        if (remaining > 3) {
            suggestions.push({
                type: 'UPGRADE',
                content: `You have Â£${remaining.toFixed(1)}m spare. Consider upgrading your cheapest midfielder to a premium option.`
            });
        }

        // Chip strategy
        suggestions.push({
            type: 'STRATEGY',
            content: 'Save your Wildcard for GW25 when fixture swings favor different teams. Use Free Hit for blank gameweeks.'
        });

        return suggestions;
    }

    suggestOptimalFormation(players) {
        // Analyze player strengths
        const positions = {
            DEF: players.filter(p => p.position === 'DEF').length,
            MID: players.filter(p => p.position === 'MID').length,
            FWD: players.filter(p => p.position === 'FWD').length
        };

        // Suggest formation based on player distribution
        if (positions.MID >= 5) return '3-5-2';
        if (positions.FWD >= 3) return '3-4-3';
        if (positions.DEF >= 5) return '5-3-2';

        return '4-4-2'; // Default balanced
    }

    // Team management functions
    saveTeam() {
        const teamData = {
            players: this.team.players,
            formation: this.team.formation,
            timestamp: new Date().toISOString()
        };

        localStorage.setItem('fpl_saved_team', JSON.stringify(teamData));
        this.showTip('SAVED', 'Team saved successfully!');
    }

    loadTeam() {
        const saved = localStorage.getItem('fpl_saved_team');

        if (!saved) {
            this.showTip('NO_SAVE', 'No saved team found.');
            return;
        }

        const teamData = JSON.parse(saved);
        this.team.players = teamData.players;
        this.team.formation = teamData.formation;

        this.updateFormation(this.team.formation);
        this.updateBudget();
        this.renderPlayerList();
        this.analyzeTeam();

        this.showTip('LOADED', 'Team loaded successfully!');
    }

    loadSavedTeam() {
        // Auto-load on init
        const saved = localStorage.getItem('fpl_saved_team');
        if (saved) {
            const teamData = JSON.parse(saved);
            this.team.players = teamData.players;
            this.team.formation = teamData.formation;

            this.updateFormation(this.team.formation);
            this.updateBudget();
            this.renderPlayerList();
            this.analyzeTeam();
        }
    }

    shareTeam() {
        const players = this.team.players.filter(p => p);

        if (players.length === 0) {
            this.showTip('EMPTY', 'Add players to share your team.');
            return;
        }

        const teamText = `My FPL Team (${this.team.formation}):\n\n` +
            `GK: ${this.team.players[0]?.name || 'Empty'}\n` +
            `DEF: ${this.team.players.slice(1, 6).filter(p => p && p.position === 'DEF').map(p => p.name).join(', ')}\n` +
            `MID: ${this.team.players.slice(1, 11).filter(p => p && p.position === 'MID').map(p => p.name).join(', ')}\n` +
            `FWD: ${this.team.players.slice(1, 11).filter(p => p && p.position === 'FWD').map(p => p.name).join(', ')}\n` +
            `\nBench: ${this.team.players.slice(11, 15).filter(p => p).map(p => p.name).join(', ')}\n` +
            `\nTeam Value: Â£${this.team.spent.toFixed(1)}m\n` +
            `\nBuilt with EPL News Hub Team Picker`;

        if (navigator.share) {
            navigator.share({
                title: 'My FPL Team',
                text: teamText,
                url: window.location.href
            });
        } else {
            // Copy to clipboard
            navigator.clipboard.writeText(teamText);
            this.showTip('COPIED', 'Team copied to clipboard!');
        }
    }

    exportTeam() {
        const teamData = {
            players: this.team.players,
            formation: this.team.formation,
            budget: this.team.budget,
            spent: this.team.spent,
            timestamp: new Date().toISOString()
        };

        const blob = new Blob([JSON.stringify(teamData, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);

        const a = document.createElement('a');
        a.href = url;
        a.download = `fpl-team-${Date.now()}.json`;
        a.click();

        URL.revokeObjectURL(url);
        this.showTip('EXPORTED', 'Team exported successfully!');
    }

    async importFromFPL() {
        const teamId = prompt('Enter your FPL Team ID:');

        if (!teamId) return;

        try {
            // In production, this would fetch from FPL API
            this.showTip('IMPORT', 'FPL import requires API access. Feature coming soon!');
        } catch (error) {
            this.showTip('ERROR', 'Failed to import team. Please try again.');
        }
    }

    clearTeam() {
        if (confirm('Are you sure you want to clear your entire team?')) {
            this.team.players = new Array(15).fill(null);
            this.team.spent = 0;

            this.updateFormation(this.team.formation);
            this.updateBudget();
            this.renderPlayerList();
            this.analyzeTeam();

            this.showTip('CLEARED', 'Team cleared successfully!');
        }
    }
}

// Initialize the team picker
document.addEventListener('DOMContentLoaded', () => {
    window.teamPicker = new FPLTeamPicker();
});
