  1411→
  1412→            setupEventListeners() {
  1413→                // Price filter
  1414→                document.getElementById('priceFilter').addEventListener('input', (e) => {
  1415→                    document.getElementById('priceValue').textContent = `£${(e.target.value / 10).toFixed(1)}m`;
  1416→                });
  1417→
  1418→                // Form filter
