  1420→                    document.getElementById('formValue').textContent = e.target.value;
  1421→                });
  1422→
  1423→                // Auto-refresh on tab focus
  1424→                document.addEventListener('visibilitychange', () => {
  1425→                    if (!document.hidden) {
  1426→                        this.loadPlayerData();
