  1434→
  1435→            displayPlayers() {
  1436→                const container = document.getElementById('playerCards');
  1437→                const filteredPlayers = this.filterPlayers();
  1438→                
  1439→                container.innerHTML = '';
  1440→                
  1441→                filteredPlayers.slice(0, 50).forEach(player => {
  1442→                    const card = this.createPlayerCard(player);
  1443→                    container.appendChild(card);
  1444→                });
  1445→            }
  1446→
  1447→            filterPlayers() {
  1448→                const position = document.getElementById('positionFilter').value;
  1449→                const maxPrice = parseInt(document.getElementById('priceFilter').value);
  1450→                const minForm = parseFloat(document.getElementById('formFilter').value);
  1451→                const search = document.getElementById('searchPlayer').value.toLowerCase();
  1452→
  1453→                return this.allPlayers.filter(player => {
  1454→                    const matchesPosition = !position || player.element_type == position;
  1455→                    const matchesPrice = player.now_cost <= maxPrice;
  1456→                    const matchesForm = parseFloat(player.form) >= minForm;
  1457→                    const matchesSearch = !search || 
  1458→                        `${player.first_name} ${player.second_name}`.toLowerCase().includes(search);
  1459→                    
  1460→                    return matchesPosition && matchesPrice && matchesForm && matchesSearch;
  1461→                }).sort((a, b) => b.total_points - a.total_points);
  1462→            }
  1463→
  1464→            createPlayerCard(player) {
  1465→                const team = this.teams.find(t => t.id === player.team);
  1466→                const card = document.createElement('div');
  1467→                card.className = 'market-player-card';
  1468→                
  1469→                const positionBadges = ['', 'badge-gkp', 'badge-def', 'badge-mid', 'badge-fwd'];
  1470→                const positionNames = ['', 'GKP', 'DEF', 'MID', 'FWD'];
  1471→                
  1472→                card.innerHTML = `
