  1478→                                <span class="position-badge ${positionBadges[player.element_type]}">
  1479→                                    ${positionNames[player.element_type]}
  1480→                                </span>
  1481→                            </div>
  1482→                        </div>
  1483→                        <div class="player-card-price">£${(player.now_cost / 10).toFixed(1)}m</div>
  1484→                    </div>
  1485→                    
  1486→                    <div class="player-stats-grid">
  1487→                        <div class="stat-box">
  1488→                            <div class="stat-value">${player.total_points}</div>
  1489→                            <div class="stat-label">Points</div>
  1490→                        </div>
  1491→                        <div class="stat-box">
  1492→                            <div class="stat-value">${player.form}</div>
  1493→                            <div class="stat-label">Form</div>
  1494→                        </div>
  1495→                        <div class="stat-box">
  1496→                            <div class="stat-value">${player.selected_by_percent}%</div>
  1497→                            <div class="stat-label">TSB</div>
  1498→                        </div>
  1499→                    </div>
  1500→                    
  1501→                    <div class="form-indicator">
  1502→                        ${this.generateFormDots(parseFloat(player.form))}
  1503→                    </div>
  1504→                    
  1505→                    <div class="action-buttons">
  1506→                        <button class="btn btn-success" onclick="transferSimPro.addPlayer(${player.id})">
  1507→                            Add to Squad
  1508→                        </button>
  1509→                        <button class="btn btn-primary" onclick="transferSimPro.comparePlayer(${player.id})">
  1510→                            Compare
  1511→                        </button>
  1512→                    </div>
  1513→                `;
  1514→                
  1515→                return card;
  1516→            }
  1517→
  1518→            generateFormDots(form) {
  1519→                let dots = '';
  1520→                for (let i = 0; i < 5; i++) {
  1521→                    const threshold = (i + 1) * 2;
  1522→                    let className = 'form-dot form-bad';
  1523→                    if (form >= threshold - 0.5) className = 'form-dot form-good';
  1524→                    else if (form >= threshold - 1.5) className = 'form-dot form-average';
  1525→                    dots += `<div class="${className}"></div>`;
  1526→                }
  1527→                return dots;
  1528→            }
  1529→
  1530→            selectSlot(position, index) {
  1531→                // Highlight selected slot
  1532→                document.querySelectorAll('.player-slot').forEach(slot => {
  1533→                    slot.classList.remove('selected');
  1534→                });
  1535→                
  1536→                const slot = document.querySelector(`[data-position="${position}"][data-index="${index}"]`);
  1537→                slot.classList.add('selected');
  1538→                
  1539→                // Filter players for this position
  1540→                document.getElementById('positionFilter').value = position;
  1541→                this.displayPlayers();
  1542→            }
  1543→
  1544→            addPlayer(playerId) {
  1545→                const player = this.allPlayers.find(p => p.id === playerId);
  1546→                if (!player) return;
  1547→
  1548→                const position = player.element_type;
  1549→                const maxPlayers = { 1: 2, 2: 5, 3: 5, 4: 3 };
  1550→                
  1551→                // Check if position is full
  1552→                if (this.currentSquad[position].length >= maxPlayers[position]) {
  1553→                    this.showNotification('Position is full! Remove a player first.', 'error');
  1554→                    return;
  1555→                }
  1556→
  1557→                // Check budget
  1558→                if (player.now_cost > this.budget) {
  1559→                    this.showNotification('Insufficient budget!', 'error');
  1560→                    return;
  1561→                }
  1562→
  1563→                // Add player
  1564→                this.currentSquad[position].push(player);
  1565→                this.budget -= player.now_cost;
  1566→                
  1567→                // Add to transfer history
  1568→                this.addToHistory('in', player);
  1569→                
  1570→                // Update display
  1571→                this.updateSquadDisplay();
  1572→                this.updateDisplay();
  1573→                this.updateCharts();
  1574→                
  1575→                this.showNotification(`Added ${player.first_name} ${player.second_name}`, 'success');
  1576→            }
  1577→
  1578→            removePlayer(playerId) {
  1579→                for (let position in this.currentSquad) {
  1580→                    const index = this.currentSquad[position].findIndex(p => p.id === playerId);
  1581→                    if (index >= 0) {
  1582→                        const player = this.currentSquad[position][index];
  1583→                        this.currentSquad[position].splice(index, 1);
  1584→                        this.budget += player.now_cost;
  1585→                        
  1586→                        this.addToHistory('out', player);
  1587→                        this.updateSquadDisplay();
  1588→                        this.updateDisplay();
  1589→                        this.updateCharts();
  1590→                        
  1591→                        this.showNotification(`Removed ${player.first_name} ${player.second_name}`, 'info');
  1592→                        break;
  1593→                    }
  1594→                }
  1595→            }
  1596→
  1597→            updateSquadDisplay() {
  1598→                const positions = {
  1599→                    1: 'gkp-slots',
  1600→                    2: 'def-slots',
  1601→                    3: 'mid-slots',
  1602→                    4: 'fwd-slots'
  1603→                };
  1604→
  1605→                Object.entries(positions).forEach(([posType, containerId]) => {
  1606→                    const container = document.getElementById(containerId);
  1607→                    const slots = container.querySelectorAll('.player-slot');
  1608→                    const players = this.currentSquad[posType];
  1609→                    
  1610→                    slots.forEach((slot, index) => {
  1611→                        if (players[index]) {
  1612→                            const player = players[index];
  1613→                            const team = this.teams.find(t => t.id === player.team);
  1614→                            slot.innerHTML = `
