  1620→                            `;
  1621→                            slot.onclick = () => this.removePlayer(player.id);
  1622→                            slot.style.background = 'linear-gradient(135deg, #d4edda, #ffffff)';
  1623→                        } else {
  1624→                            slot.innerHTML = `
  1625→                                <div class="player-info">
