  1627→                                    <div class="player-details">Click to add player</div>
  1628→                                </div>
  1629→                                <div class="player-price">-</div>
  1630→                            `;
  1631→                            slot.onclick = () => this.selectSlot(posType, index);
  1632→                            slot.style.background = '';
  1633→                        }
  1634→                    });
  1635→                });
  1636→            }
  1637→
  1638→            updateDisplay() {
  1639→                // Update budget
