  1643→
  1644→            addToHistory(type, player) {
  1645→                const team = this.teams.find(t => t.id === player.team);
  1646→                const history = document.getElementById('transferHistory');
  1647→                
  1648→                if (history.querySelector('.history-item').textContent.includes('No transfers yet')) {
  1649→                    history.innerHTML = '';
