  1656→                    <div>
  1657→                        <div style="font-weight: 700;">${player.first_name} ${player.second_name}</div>
  1658→                        <div style="color: #666; font-size: 0.9rem;">
  1659→                            ${team?.name} • £${(player.now_cost / 10).toFixed(1)}m
  1660→                        </div>
  1661→                    </div>
  1662→                `;
  1663→                
  1664→                history.insertBefore(item, history.firstChild);
  1665→                
  1666→                // Keep only last 10 transfers
  1667→                while (history.children.length > 10) {
