  1718→
  1719→            updateCharts() {
  1720→                // Calculate values and points by position
  1721→                const values = [0, 0, 0, 0];
  1722→                const points = [0, 0, 0, 0];
  1723→                
  1724→                for (let pos = 1; pos <= 4; pos++) {
  1725→                    this.currentSquad[pos].forEach(player => {
  1726→                        values[pos - 1] += player.now_cost / 10;
  1727→                        points[pos - 1] += player.total_points;
  1728→                    });
  1729→                }
  1730→                
  1731→                // Update charts
  1732→                this.charts.value.data.datasets[0].data = values;
  1733→                this.charts.value.update();
