  1869→            calculateExpectedPointsForGameweek(player) {
  1870→                // Advanced expected points calculation for current gameweek
  1871→                const form = parseFloat(player.form) || 0;
  1872→                const totalPoints = player.total_points || 0;
  1873→                const gamesPlayed = player.starts || 1;
  1874→                const avgPoints = totalPoints / Math.max(gamesPlayed, 1);
  1875→                
  1876→                // Expected goals and assists contribute to attacking returns
  1877→                const xG = parseFloat(player.expected_goals) || 0;
  1878→                const xA = parseFloat(player.expected_assists) || 0;
  1879→                const xGI = (xG + xA) / Math.max(gamesPlayed, 1);
  1880→                
  1881→                // Position-based scoring potential
  1882→                const positionMultiplier = {
  1883→                    1: 0.8,  // GKP - consistent but lower ceiling
  1884→                    2: 1.0,  // DEF - clean sheets + occasional goals
  1885→                    3: 1.2,  // MID - goals, assists, clean sheets
  1886→                    4: 1.3   // FWD - highest goal threat
  1887→                }[player.element_type] || 1;
  1888→                
  1889→                // Calculate base expected points
  1890→                let expectedPoints = (
  1891→                    form * 0.45 +           // Recent form (45%)
  1892→                    avgPoints * 0.25 +      // Season average (25%)
  1893→                    xGI * 10 * 0.20 +       // Expected goal involvement (20%)
  1894→                    (player.bonus || 0) * 0.10  // Bonus point history (10%)
  1895→                ) * positionMultiplier;
  1896→                
  1897→                // Fixture difficulty adjustment (simplified - would need real fixture data)
  1898→                const fixtureBonus = Math.random() * 0.2 + 0.9; // 0.9 to 1.1 multiplier
  1899→                expectedPoints *= fixtureBonus;
  1900→                
  1901→                // Differential bonus for low ownership high performers
  1902→                if (player.selected_by_percent < 10 && expectedPoints > 5) {
  1903→                    expectedPoints *= 1.15;
  1904→                }
  1905→                
  1906→                // Penalty taker bonus
  1907→                if (player.penalties_order && player.penalties_order <= 1) {
  1908→                    expectedPoints *= 1.1;
  1909→                }
  1910→                
  1911→                return Math.max(expectedPoints, 2); // Minimum 2 points
  1912→            }
  1913→            
  1914→            buildOptimalTeamForFormation(sortedPlayers, formation) {
  1915→                const team = { 1: [], 2: [], 3: [], 4: [] };
  1916→                let remainingBudget = 1000;
  1917→                const usedPlayerIds = new Set();
  1918→                const maxPerTeam = 3; // FPL rule: max 3 players from same team
  1919→                const teamCounts = {};
  1920→                
  1921→                // Requirements for each position
  1922→                const requirements = {
  1923→                    1: formation.gkp,
  1924→                    2: formation.def,
  1925→                    3: formation.mid,
  1926→                    4: formation.fwd
  1927→                };
  1928→                
  1929→                // Fill each position with best available players
  1930→                for (let position = 1; position <= 4; position++) {
  1931→                    const needed = requirements[position];
  1932→                    const positionPlayers = sortedPlayers.filter(p => 
  1933→                        p.element_type === position && 
  1934→                        !usedPlayerIds.has(p.id)
  1935→                    );
  1936→                    
  1937→                    // Determine premium vs budget split
  1938→                    const premiumCount = position === 1 ? 1 : Math.min(Math.ceil(needed * 0.4), 2);
  1939→                    const budgetCount = needed - premiumCount;
  1940→                    
  1941→                    // Add premium players first (highest expected points)
  1942→                    let added = 0;
  1943→                    for (const player of positionPlayers) {
  1944→                        if (added >= premiumCount) break;
  1945→                        
  1946→                        const teamCount = teamCounts[player.team] || 0;
  1947→                        if (teamCount >= maxPerTeam) continue;
  1948→                        
  1949→                        if (player.now_cost <= remainingBudget) {
  1950→                            team[position].push(player);
  1951→                            remainingBudget -= player.now_cost;
  1952→                            usedPlayerIds.add(player.id);
  1953→                            teamCounts[player.team] = teamCount + 1;
  1954→                            added++;
  1955→                        }
  1956→                    }
  1957→                    
  1958→                    // Add value players (best points per million)
  1959→                    const valuePlayers = positionPlayers
  1960→                        .filter(p => !usedPlayerIds.has(p.id))
  1961→                        .map(p => ({
  1962→                            ...p,
  1963→                            value: p.expectedPoints / (p.now_cost / 10)
  1964→                        }))
  1965→                        .sort((a, b) => b.value - a.value);
  1966→                    
  1967→                    for (const player of valuePlayers) {
  1968→                        if (team[position].length >= needed) break;
  1969→                        
  1970→                        const teamCount = teamCounts[player.team] || 0;
  1971→                        if (teamCount >= maxPerTeam) continue;
  1972→                        
  1973→                        if (player.now_cost <= remainingBudget) {
  1974→                            team[position].push(player);
  1975→                            remainingBudget -= player.now_cost;
  1976→                            usedPlayerIds.add(player.id);
  1977→                            teamCounts[player.team] = teamCount + 1;
  1978→                        }
  1979→                    }
  1980→                    
  1981→                    // Fill remaining slots with cheapest valid players
  1982→                    if (team[position].length < needed) {
  1983→                        const cheapest = positionPlayers
  1984→                            .filter(p => {
  1985→                                const teamCount = teamCounts[p.team] || 0;
  1986→                                return !usedPlayerIds.has(p.id) && teamCount < maxPerTeam;
  1987→                            })
  1988→                            .sort((a, b) => a.now_cost - b.now_cost);
  1989→                        
  1990→                        for (const player of cheapest) {
  1991→                            if (team[position].length >= needed) break;
  1992→                            
  1993→                            const teamCount = teamCounts[player.team] || 0;
  1994→                            team[position].push(player);
  1995→                            remainingBudget -= player.now_cost;
  1996→                            usedPlayerIds.add(player.id);
  1997→                            teamCounts[player.team] = teamCount + 1;
  1998→                        }
  1999→                    }
  2000→                }
  2001→                
  2002→                return team;
  2003→            }
  2004→            
  2005→            calculateTeamExpectedPoints(team) {
  2006→                let total = 0;
  2007→                Object.values(team).forEach(position => {
  2008→                    position.forEach(player => {
  2009→                        total += this.calculateExpectedPointsForGameweek(player);
  2010→                    });
  2011→                });
  2012→                return total;
  2013→            }
