   764→    <script>
   765→        class PlayerPredictor {
   766→            constructor() {
   767→                this.fplService = new FPLHtmlDataService();
   768→                this.allPlayers = [];
   769→                this.teams = [];
   770→                this.filteredPlayers = [];
   771→                this.selectedPlayer = null;
   772→                this.predictions = {};
   773→            }
   774→
   775→            async initialize() {
   776→                try {
   777→                    const bootstrap = await this.fplService.getBootstrapData();
   778→                    
   779→                    // Check if we got valid data
   780→                    if (bootstrap && bootstrap.elements && bootstrap.elements.length > 0) {
   781→                        this.allPlayers = bootstrap.elements;
   782→                        this.teams = bootstrap.teams;
   783→                        
   784→                        this.populateTeamFilter();
   785→                        this.setupEventListeners();
   786→                        await this.performInitialSearch();
   787→                        this.updateLiveIndicator();
   788→                        
   789→                        // Auto-refresh every 2 minutes
   790→                        setInterval(() => this.refreshData(), 2 * 60 * 1000);
   791→                    } else {
   792→                        // Got empty or invalid data
   793→                        console.warn('Received empty bootstrap data, using offline mode');
   794→                        this.updateLiveIndicator(false);
   795→                        this.loadMockPlayers();
   796→                    }
   797→                } catch (error) {
   798→                    console.error('Failed to initialize player predictor:', error);
   799→                    // Silently continue with mock data - no alert
   800→                    this.updateLiveIndicator(false);
   801→                    // Initialize with mock data
   802→                    this.loadMockPlayers();
   803→                }
   804→            }
   805→
   806→            async refreshData() {
   807→                console.log('Refreshing live data...');
   808→                document.getElementById('data-status').textContent = 'REFRESHING...';
   809→                
   810→                try {
   811→                    const bootstrap = await this.fplService.getBootstrapData(true); // Force refresh
   812→                    this.allPlayers = bootstrap.elements;
   813→                    this.teams = bootstrap.teams;
   814→                    
   815→                    // Re-run search with updated data
