    /**
     * Simulate user logout (for testing)
     */
    simulateLogout() {
        localStorage.removeItem('userLoggedIn');
        localStorage.removeItem('membershipLevel');
        localStorage.removeItem('dailyUsage');
        this.userStatus = this.getUserStatus();
        location.reload();
    }
