                isLoggedIn: isLoggedIn,
                membershipLevel: parsed.tier || membershipLevel,
                dailyUsage: this.getDailyUsage(),
                isActive: parsed.status === 'active'
            };
        } catch (error) {
            console.error('Error parsing subscription data:', error);
