    const expectedScore = (expectedGoals * 4) + (expectedAssists * 3);
    const consistencyScore = starts > 0 ? (minutes / starts) / 90 : 0;
    const trendScore = transfersIn > transfersOut ? 1.1 : transfersOut > transfersIn ? 0.9 : 1;

    // Base score combining multiple factors
    score = valueScore * 0.4 + formScore * 0.2 + expectedScore * 0.25 + totalPoints * 0.1 + consistencyScore * 0.05;

    // Strategy-specific adjustments
    if (strategy === 'best') {
        // Premium strategy - prioritize top performers regardless of price
        score = totalPoints * 0.6 + formScore * 0.2 + expectedScore * 0.15 + valueScore * 0.05;
        if (totalPoints > 150) score *= 1.2; // Boost elite players
        if (nowCost >= 100) score *= 1.1; // Don't penalize expensive players
    } else if (strategy === 'budget') {
        // Value strategy - heavily weight points per million
        score = valueScore * 0.7 + formScore * 0.15 + expectedScore * 0.1 + totalPoints * 0.05;
        if (nowCost < 60) score *= 1.3; // Heavily boost cheap players
        if (valueScore > 20) score *= 1.2; // Boost exceptional value
    } else if (strategy === 'wildcard') {
        // Attack-focused strategy
        score = expectedScore * 0.4 + totalPoints * 0.3 + formScore * 0.2 + valueScore * 0.1;
        if (player.element_type === 4 || player.element_type === 3) score *= 1.15; // Boost attacking players
    } else if (strategy === 'balanced') {
        // Balanced approach
        score = valueScore * 0.3 + totalPoints * 0.25 + formScore * 0.2 + expectedScore * 0.2 + consistencyScore * 0.05;
    }

    // Apply trend multiplier
    score *= trendScore;

    // Form and playing time adjustments
    if (minutes > 2500) score *= 1.2; // Regular starters
    if (minutes > 2000) score *= 1.1;
    if (starts > 25) score *= 1.15; // Consistent starters
    if (starts > 20) score *= 1.1;
    if (form > 8) score *= 1.15; // Excellent form
    if (form > 6) score *= 1.1; // Good form

    // Ownership and fixture considerations
    if (preferences.differentialPicks) {
        if (selectedBy < 5) score *= 1.3; // High differential boost
        else if (selectedBy < 15) score *= 1.15; // Medium differential boost
        else if (selectedBy > 40) score *= 0.9; // Penalize template picks
    }

    if (preferences.considerFixtures && player.team) {
        // Enhanced fixture difficulty consideration
        const easyFixtureTeams = [1, 3, 4, 8, 9]; // Teams with easier upcoming fixtures
        const hardFixtureTeams = [2, 5, 6, 7, 10]; // Teams with harder fixtures

        if (easyFixtureTeams.includes(player.team)) {
            score *= 1.2; // Boost for easy fixtures
        } else if (hardFixtureTeams.includes(player.team)) {
            score *= 0.85; // Penalize for hard fixtures
        }
    }

    // Position-specific adjustments
    if (player.element_type === 1) { // Goalkeepers
        // Focus on clean sheet potential and saves
        if (totalPoints > 100) score *= 1.1;
    } else if (player.element_type === 2) { // Defenders
        // Balance attacking returns with defensive stability
        if (expectedGoals > 2 || expectedAssists > 3) score *= 1.15;
    } else if (player.element_type === 3) { // Midfielders
        // Versatile scoring - goals and assists
        if (expectedGoals > 5 && expectedAssists > 3) score *= 1.2;
    } else if (player.element_type === 4) { // Forwards
        // Goal-focused
        if (expectedGoals > 8) score *= 1.25;
    }

    // Ensure score is a valid number and apply floor
    if (isNaN(score) || !isFinite(score)) {
        score = 0;
    }

    // Minimum score for active players
    if (minutes > 500 && score < 1) {
        score = 1;
    }

    return Math.max(0, score);
}

